   601  
   602  startMonitoring() {
   603    // Monitor render performance
   604    this.map.on('render', () => {
   605      const renderTime = performance.now();
   606      this.metrics.renderTime.push(renderTime);
   607      
   608      // Keep last 100 samples
   609      if (this.metrics.renderTime.length > 100) {
   610        this.metrics.renderTime.shift();
   611      }
   612    });
   613    
   614    // Monitor FPS
   615    let lastTime = performance.now();
   616    const measureFPS = () => {
   617      const currentTime = performance.now();
   618      const fps = 1000 / (currentTime - lastTime);
   619      this.metrics.fps.push(fps);
   620      
   621      if (this.metrics.fps.length > 60) {
   622        this.metrics.fps.shift();
   623      }
   624      
   625      lastTime = currentTime;
   626      requestAnimationFrame(measureFPS);
   627    };
   628    measureFPS();
   629  }
   630  
   631  getAverageMetrics() {
