   633      avgRenderTime: this.average(this.metrics.renderTime),
   634      avgFPS: this.average(this.metrics.fps)
   635    };
   636  }
   637  
   638  average(arr) {
   639    return arr.reduce((a, b) => a + b, 0) / arr.length;
   640  }
