Comprehensive Error Management
javascriptclass MapboxErrorHandler {
  constructor(map) {
    this.map = map;
    this.setupErrorHandlers();
  }

  setupErrorHandlers() {
    this.map.on('error', (e) => {
      console.error('Map error:', e.error);

      if (e.error.message.includes('Failed to load style')) {
        this.loadFallbackStyle();
      } else if (e.error.message.includes('Access token')) {
        this.handleAuthError();
      } else if (e.error.message.includes('Network')) {
        this.handleNetworkError();
      }

      this.logError(e.error);
    });

    this.map.on('sourcedataloading', (e) => {
      setTimeout(() => {
        if (this.map.isSourceLoaded(e.sourceId) === false) {
          console.warn(`Source ${e.sourceId} is taking long to load`);
          this.handleSlowSource(e.sourceId);
        }
      }, 5000);
    });
  }

  loadFallbackStyle() {
    this.map.setStyle('mapbox://styles/mapbox/light-v11');
  }

  handleAuthError() {
    console.error('Authentication failed. Check access token.');
  }

  handleNetworkError() {
    console.warn('Network error. Switching to offline mode.');
  }

  handleSlowSource(sourceId) {
    const style = this.map.getStyle();
    style.layers.forEach(layer => {
      if (layer.source === sourceId) {
        this.map.removeLayer(layer.id);
      }
    });
    if (this.map.getSource(sourceId)) {
      this.map.removeSource(sourceId);
    }
  }

  logError(error) {
    // Send to monitoring service
  }
}
Accessibility
Keyboard Navigation Implementation
javascriptclass AccessibleMap {
  constructor(container, map) {
    this.container = container;
    this.map = map;
    this.initializeAccessibility();
  }

  initializeAccessibility() {
    this.container.setAttribute('role', 'application');
    this.container.setAttribute('aria-label', 'Interactive map');

    this.setupKeyboardNavigation();
    this.setupAnnouncements();
  }

  setupKeyboardNavigation() {
    document.addEventListener('keydown', (e) => {
      if (!this.container.contains(document.activeElement)) return;

      const step = e.shiftKey ? 10 : 1;

      switch(e.key) {
        case 'ArrowUp':
          e.preventDefault();
          this.map.panBy([0, -50 * step]);
          this.announce('Panned north');
          break;
        case 'ArrowDown':
          e.preventDefault();
          this.map.panBy([0, 50 * step]);
          this.announce('Panned south');
          break;
        case 'ArrowLeft':
          e.preventDefault();
          this.map.panBy([-50 * step, 0]);
          this.announce('Panned west');
          break;
        case 'ArrowRight':
          e.preventDefault();
          this.map.panBy([50 * step, 0]);
          this.announce('Panned east');
          break;
        case '+':
          e.preventDefault();
          this.map.zoomIn();
          this.announce(`Zoomed to level ${Math.round(this.map.getZoom())}`);
          break;
        case '-':
          e.preventDefault();
          this.map.zoomOut();
          this.announce(`Zoomed to level ${Math.round(this.map.getZoom())}`);
          break;
      }
    });
  }

  setupAnnouncements() {
    this.announcer = document.createElement('div');
    this.announcer.setAttribute('role', 'status');
    this.announcer.setAttribute('aria-live', 'polite');
    this.announcer.className = 'sr-only';
    this.announcer.style.position = 'absolute';
    this.announcer.style.left = '-10000px';
    document.body.appendChild(this.announcer);
  }

  announce(message) {
    this.announcer.textContent = message;
    setTimeout(() => {
      this.announcer.textContent = '';
    }, 1000);
  }
}
Security
Secure Token Management
javascriptclass MapboxSecurityManager {
  constructor() {
    this.tokenEndpoint = '/api/mapbox-token';
    this.tokenCache = null;
    this.tokenExpiry = null;
  }

  async getToken() {
    if (this.tokenCache && this.tokenExpiry > Date.now()) {
      return this.tokenCache;
    }

    try {
      const response = await fetch(this.tokenEndpoint, {
        method: 'POST',
        credentials: 'include',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          scopes: ['styles:read', 'fonts:read', 'datasets:read']
        })
      });

      if (!response.ok) {
        throw new Error('Failed to fetch token');
      }

      const data = await response.json();

      this.tokenCache = data.token;
      this.tokenExpiry = Date.now() + (data.expiresIn * 1000);

      return this.tokenCache;
    } catch (error) {
      console.error('Token fetch failed:', error);
      return process.env.REACT_APP_MAPBOX_PUBLIC_TOKEN;
    }
  }

  sanitizeInput(input) {
    const cleaned = input
      .replace(/<script[^>]*>.*?<\/script>/gi, '')
      .replace(/<[^>]+>/g, '')
      .trim();

    if (this.isCoordinate(cleaned)) {
      return this.validateCoordinate(cleaned);
    }

    return cleaned;
  }

  validateCoordinate(coord) {
    const [lng, lat] = coord.split(',').map(Number);

    if (lat < -90 || lat > 90 || lng < -180 || lng > 180) {
      throw new Error('Invalid coordinates');
    }

    return { lng, lat };
  }
}
