Combining Local and Remote Data
javascript// Integration pattern for local GeoJSON with Mapbox sources
class DataIntegrationManager {
  constructor(map) {
    this.map = map;
    this.localData = new Map();
    this.remoteData = new Map();
  }

  async loadCombinedData(localGeoJSON, mapboxSourceId) {
    // Add local data source
    this.map.addSource('local-data', {
      type: 'geojson',
      data: localGeoJSON
    });

    // Add Mapbox vector source
    this.map.addSource('mapbox-data', {
      type: 'vector',
      url: `mapbox://${mapboxSourceId}`
    });

    // Create unified visualization
    this.createUnifiedLayers();
  }

  createUnifiedLayers() {
    // Local data layer with custom styling
    this.map.addLayer({
      id: 'local-points',
      type: 'circle',
      source: 'local-data',
      paint: {
        'circle-color': '#ff0000',
        'circle-radius': 8,
        'circle-stroke-width': 2,
        'circle-stroke-color': '#ffffff'
      }
    });

    // Mapbox data layer
    this.map.addLayer({
      id: 'mapbox-points',
      type: 'circle',
      source: 'mapbox-data',
      'source-layer': 'points',
      paint: {
        'circle-color': '#0000ff',
        'circle-radius': 6
      }
    });
  }

  updateLocalData(newData) {
    const source = this.map.getSource('local-data');
    if (source) {
      source.setData(newData);
    }
  }
}
Real-time Updates with WebSockets
javascriptclass RealtimeMapUpdater {
  constructor(map, wsUrl) {
    this.map = map;
    this.ws = new WebSocket(wsUrl);
    this.setupWebSocket();
  }

  setupWebSocket() {
    this.ws.onmessage = (event) => {
      const update = JSON.parse(event.data);
      this.applyUpdate(update);
    };

    this.ws.onerror = (error) => {
      console.error('WebSocket error:', error);
      this.reconnect();
    };
  }

  applyUpdate(update) {
    switch(update.type) {
      case 'feature-add':
        this.addFeature(update.feature);
        break;
      case 'feature-update':
        this.updateFeature(update.feature);
        break;
      case 'feature-remove':
        this.removeFeature(update.featureId);
        break;
    }
  }

  addFeature(feature) {
    const source = this.map.getSource('realtime-data');
    if (source) {
      const data = source._data;
      data.features.push(feature);
      source.setData(data);
    }
  }

  reconnect() {
    setTimeout(() => {
      this.ws = new WebSocket(this.wsUrl);
      this.setupWebSocket();
    }, 5000);
  }
}
