Issue: Race Conditions
javascript//  WRONG: Not waiting for style to load
map.setStyle('new-style');
map.addLayer({ /* ... */ }); // May fail

//  CORRECT: Wait for style load
map.setStyle('new-style');
map.once('style.load', () => {
  map.addLayer({ /* ... */ });
});
Performance Metrics
Monitoring Map Performance
javascriptclass MapPerformanceMonitor {
  constructor(map) {
    this.map = map;
    this.metrics = {
      renderTime: [],
      sourceLoadTime: {},
      fps: []
    };

    this.startMonitoring();
  }

  startMonitoring() {
    // Monitor render performance
    this.map.on('render', () => {
      const renderTime = performance.now();
      this.metrics.renderTime.push(renderTime);

      // Keep last 100 samples
      if (this.metrics.renderTime.length > 100) {
        this.metrics.renderTime.shift();
      }
    });

    // Monitor FPS
    let lastTime = performance.now();
    const measureFPS = () => {
      const currentTime = performance.now();
      const fps = 1000 / (currentTime - lastTime);
      this.metrics.fps.push(fps);

      if (this.metrics.fps.length > 60) {
        this.metrics.fps.shift();
      }

      lastTime = currentTime;
      requestAnimationFrame(measureFPS);
    };
    measureFPS();
  }

  getAverageMetrics() {
    return {
      avgRenderTime: this.average(this.metrics.renderTime),
      avgFPS: this.average(this.metrics.fps)
    };
  }

  average(arr) {
    return arr.reduce((a, b) => a + b, 0) / arr.length;
  }
}

