// Layer Manager Class for programmatic control
class MapboxLayerManager {
  private map: mapboxgl.Map;
  private dynamicLayers: Map<string, DynamicLayerConfig>;
  private styleLayers: Map<string, StyleLayerConfig>;

  constructor(map: mapboxgl.Map) {
    this.map = map;
    this.dynamicLayers = new Map();
    this.styleLayers = new Map();
    this.initializeStyleLayers();
  }

  // Discover and catalog style layers
  private initializeStyleLayers() {
    const style = this.map.getStyle();
    if (style?.layers) {
      style.layers.forEach(layer => {
        this.styleLayers.set(layer.id, {
          id: layer.id,
          type: layer.type,
          visible: layer.layout?.visibility !== 'none',
          metadata: layer.metadata || {}
        });
      });
    }
  }

  // Add dynamic GeoJSON layer with proper ordering
  addDynamicLayer(config: DynamicLayerConfig) {
    const { id, source, type, paint, beforeId } = config;

    // Ensure source exists
    if (!this.map.getSource(source.id)) {
      this.map.addSource(source.id, source.config);
    }

    // Add layer with proper positioning
    this.map.addLayer({
      id,
      source: source.id,
      type,
      paint,
      layout: { visibility: 'visible' }
    }, beforeId || this.getTopLayerId());

    this.dynamicLayers.set(id, config);
  }

  // Toggle any layer (style or dynamic)
  toggleLayer(layerId: string, visible?: boolean) {
    const visibility = visible ?? 
      (this.map.getLayoutProperty(layerId, 'visibility') !== 'visible');

    this.map.setLayoutProperty(
      layerId, 
      'visibility', 
      visibility ? 'visible' : 'none'
    );
  }

  // Batch update for performance
  batchUpdateLayers(updates: LayerUpdate[]) {
    this.map.once('idle', () => {
      updates.forEach(update => {
        if (update.type === 'visibility') {
          this.toggleLayer(update.id, update.visible);
        } else if (update.type === 'data' && update.source) {
          const source = this.map.getSource(update.source) as mapboxgl.GeoJSONSource;
          if (source) {
            source.setData(update.data);
          }
        }
      });
    });
  }
}
```

