// Zustand store for layer state
import { create } from 'zustand';
import { persist } from 'zustand/middleware';

interface LayerStore {
  visibleLayers: Set<string>;
  layerOpacity: Map<string, number>;
  layerFilters: Map<string, any>;

  toggleLayer: (layerId: string) => void;
  setLayerOpacity: (layerId: string, opacity: number) => void;
  setLayerFilter: (layerId: string, filter: any) => void;

  // Batch operations
  showAllLayers: () => void;
  hideAllLayers: () => void;
  toggleCategory: (category: string) => void;
}

const useLayerStore = create<LayerStore>()(
  persist(
    (set, get) => ({
      visibleLayers: new Set(),
      layerOpacity: new Map(),
      layerFilters: new Map(),

      toggleLayer: (layerId) => {
        set(state => {
          const newVisible = new Set(state.visibleLayers);
          if (newVisible.has(layerId)) {
            newVisible.delete(layerId);
          } else {
            newVisible.add(layerId);
          }
          return { visibleLayers: newVisible };
        });
      },

      // ... other methods
    }),
    {
      name: 'mapbox-layer-state',
      // Custom serialization for Sets and Maps
      storage: {
        getItem: (name) => {
          const str = localStorage.getItem(name);
          if (!str) return null;
          const { state } = JSON.parse(str);
          return {
            state: {
              ...state,
              visibleLayers: new Set(state.visibleLayers),
              layerOpacity: new Map(state.layerOpacity),
              layerFilters: new Map(state.layerFilters)
            }
          };
        },
        setItem: (name, value) => {
          localStorage.setItem(name, JSON.stringify({
            state: {
              ...value.state,
              visibleLayers: Array.from(value.state.visibleLayers),
              layerOpacity: Array.from(value.state.layerOpacity),
              layerFilters: Array.from(value.state.layerFilters)
            }
          }));
        },
        removeItem: (name) => localStorage.removeItem(name)
      }
    }
  )
);
