input.content:
/**
 * Mapbox Style Builder
 * 
 * This utility builds a complete map style programmatically,
 * eliminating the need for a custom Mapbox Studio style that blocks overlays.
 * 
 * Benefits:
 * - Full control over layer ordering
 * - No Studio configuration needed
 * - Easy to modify and maintain
 * - Guaranteed overlay layer support
 */

import type { StyleSpecification, LayerSpecification } from 'mapbox-gl';

// Type definitions for our custom layers
interface CustomLayerConfig {
  id: string;
  type: 'circle' | 'symbol' | 'line' | 'fill';
  source: string;
  paint?: Record<string, any>;
  layout?: Record<string, any>;
  filter?: any[];
  minzoom?: number;
  maxzoom?: number;
}

/**
 * Build a complete Mapbox style with satellite imagery and proper layer slots
 */
export function buildSatelliteStyle(): StyleSpecification {
  return {
    version: 8,
    name: 'FowlData Satellite',
    sources: {
      // Mapbox satellite imagery
      'mapbox-satellite': {
        type: 'raster',
        url: 'mapbox://mapbox.satellite',
        tileSize: 256
      },
      // Mapbox streets for labels and roads
      'mapbox-streets': {
        type: 'vector',
        url: 'mapbox://mapbox.mapbox-streets-v8'
      }
    },
    layers: [
      // Base satellite imagery
      {
        id: 'satellite',
        type: 'raster',
        source: 'mapbox-satellite',
        minzoom: 0,
        maxzoom: 22
      },

      // === BOTTOM SLOT FOR OVERLAYS ===
      // Your area/polygon layers would go here

      // Roads layer
      {
        id: 'roads',
        type: 'line',
        source: 'mapbox-streets',
        'source-layer': 'road',
        minzoom: 5,
        paint: {
          'line-color': 'rgba(255, 255, 255, 0.3)',
          'line-width': {
            base: 1.2,
            stops: [
              [5, 0.5],
              [10, 1],
              [15, 2],
              [20, 8]
            ]
          }
        }
      },

      // === MIDDLE SLOT FOR OVERLAYS ===
      // Your hunt/location points would go here

      // Place labels
      {
        id: 'place-labels',
        type: 'symbol',
        source: 'mapbox-streets',
        'source-layer': 'place_label',
        minzoom: 3,
        layout: {
          'text-field': '{name}',
          'text-font': ['DIN Pro Medium', 'Arial Unicode MS Regular'],
          'text-size': {
            base: 1,
            stops: [
              [3, 10],
              [10, 14],
              [15, 20]
            ]
          },
          'text-transform': 'uppercase',
          'text-letter-spacing': 0.1
        },
        paint: {
          'text-color': '#ffffff',
          'text-halo-color': 'rgba(0, 0, 0, 0.7)',
          'text-halo-width': 1.5
        }
      },

      // Road labels
      {
        id: 'road-labels',
        type: 'symbol',
        source: 'mapbox-streets',
        'source-layer': 'road',
        minzoom: 10,
        filter: ['has', 'name'],
        layout: {
          'symbol-placement': 'line',
          'text-field': '{name}',
          'text-font': ['DIN Pro Regular', 'Arial Unicode MS Regular'],
          'text-size': {
            base: 1,
            stops: [
              [10, 10],
              [15, 14]
            ]
          },
          'text-rotation-alignment': 'map'
        },
        paint: {
          'text-color': '#ffffff',
          'text-halo-color': 'rgba(0, 0, 0, 0.7)',
          'text-halo-width': 1
        }
      }

      // === TOP SLOT FOR OVERLAYS ===
      // Your highest priority overlays would go here
    ],
    // Global style properties
    glyphs: 'mapbox://fonts/mapbox/{fontstack}/{range}.pbf',
    sprite: 'mapbox://sprites/mapbox/satellite-streets-v12'
  };
}

/**
 * Insert a custom layer at the appropriate position in the style
 */
export function insertCustomLayer(
  map: mapboxgl.Map,
  layer: CustomLayerConfig,
  position: 'bottom' | 'middle' | 'top' = 'middle'
) {
  // Determine the correct position based on slot
  let beforeId: string | undefined;

  switch (position) {
    case 'bottom':
      // Insert above satellite but below roads
      beforeId = 'roads';
      break;
    case 'middle':
      // Insert above roads but below labels
      beforeId = 'place-labels';
      break;
    case 'top':
      // Insert at the very top (no beforeId)
      beforeId = undefined;
      break;
  }

  // Add the layer at the correct position
  map.addLayer(layer as LayerSpecification, beforeId);
}

/**
 * Build a hybrid style with satellite + streets overlay
 * This mimics the satellite-streets-v12 style but with full control
 */
export function buildHybridStyle(): StyleSpecification {
  const style = buildSatelliteStyle();

  // Add additional street details
  const streetDetailsLayers: LayerSpecification[] = [
    {
      id: 'building-footprints',
      type: 'fill',
      source: 'mapbox-streets',
      'source-layer': 'building',
      minzoom: 12,
      paint: {
        'fill-color': 'rgba(0, 0, 0, 0.3)',
        'fill-outline-color': 'rgba(0, 0, 0, 0.5)'
      }
    },
    {
      id: 'water-labels',
      type: 'symbol',
      source: 'mapbox-streets',
      'source-layer': 'water_label',
      minzoom: 5,
      layout: {
        'text-field': '{name}',
        'text-font': ['DIN Pro Italic', 'Arial Unicode MS Regular'],
        'text-size': 12
      },
      paint: {
        'text-color': '#74aee9',
        'text-halo-color': 'rgba(0, 0, 0, 0.7)',
        'text-halo-width': 1
      }
    }
  ];

  // Insert additional layers at appropriate positions
  style.layers.splice(2, 0, ...streetDetailsLayers);

  return style;
}

/**
 * Example: Add all FowlData layers programmatically
 */
export function addFowlDataLayers(
  map: mapboxgl.Map,
  huntsGeoJSON: GeoJSON.FeatureCollection,
  locationsGeoJSON: GeoJSON.FeatureCollection
) {
  // Wait for map to be ready
  if (!map.loaded()) {
    map.once('load', () => addFowlDataLayers(map, huntsGeoJSON, locationsGeoJSON));
    return;
  }

  // Add sources
  if (!map.getSource('hunts')) {
    map.addSource('hunts', {
      type: 'geojson',
      data: huntsGeoJSON,
      cluster: true,
      clusterRadius: 50,
      clusterMaxZoom: 14
    });
  }

  if (!map.getSource('locations')) {
    map.addSource('locations', {
      type: 'geojson',
      data: locationsGeoJSON
    });
  }

  // Add layers in correct positions

  // Cluster circles (middle position)
  insertCustomLayer(map, {
    id: 'hunt-clusters',
    type: 'circle',
    source: 'hunts',
    filter: ['has', 'point_count'],
    paint: {
      'circle-color': [
        'step',
        ['get', 'point_count'],
        '#51bbd6',
        10, '#f1f075',
        30, '#f28cb1'
      ],
      'circle-radius': [
        'step',
        ['get', 'point_count'],
        20, 10,
        30, 30
      ]
    }
  }, 'middle');

  // Cluster count labels (top position)
  insertCustomLayer(map, {
    id: 'hunt-cluster-count',
    type: 'symbol',
    source: 'hunts',
    filter: ['has', 'point_count'],
    layout: {
      'text-field': '{point_count_abbreviated}',
      'text-font': ['DIN Pro Medium', 'Arial Unicode MS Bold'],
      'text-size': 12
    },
    paint: {
      'text-color': '#ffffff'
    }
  }, 'top');

  // Individual hunt points (middle position)
  insertCustomLayer(map, {
    id: 'hunt-points',
    type: 'circle',
    source: 'hunts',
    filter: ['!', ['has', 'point_count']],
    paint: {
      'circle-color': [
        'case',
        ['==', ['get', 'privacy_level'], 'public'], '#22c55e',
        ['==', ['get', 'privacy_level'], 'friends'], '#3b82f6',
        '#ef4444' // private
      ],
      'circle-radius': 8,
      'circle-stroke-width': 2,
      'circle-stroke-color': '#ffffff'
    }
  }, 'middle');

  // Location markers (middle position)
  insertCustomLayer(map, {
    id: 'location-points',
    type: 'circle',
    source: 'locations',
    paint: {
      'circle-color': '#fbbf24',
      'circle-radius': 10,
      'circle-stroke-width': 3,
      'circle-stroke-color': '#ffffff'
    }
  }, 'middle');
}

/**
 * Helper to check if we should use programmatic style
 */
export function shouldUseProgrammaticStyle(customStyleUrl?: string): boolean {
  // If no custom style provided, use programmatic
  if (!customStyleUrl) return true;

  // If using a known problematic custom style, use programmatic
  const problematicStyles = [
    'clxb0kyjp025a01rd3gu3gj3v' // Your current custom style
  ];

  return problematicStyles.some(id => customStyleUrl.includes(id));
}

