   267            onChange={() => toggleLayer('locations')}
   268            className="rounded border-gray-300"
   269          />
   270          <span className="text-sm">Locations</span>
   271        </label>
   272
   273        <label className="flex items-center space-x-2 cursor-pointer">
   274          <input
   275            type="checkbox"
   276            checked={visibleLayers.clusters}
