/**
 * ProgrammaticMap Component
 * 
 * Alternative map implementation that builds all layers programmatically
 * instead of relying on a custom Mapbox Studio style.
 * 
 * This approach guarantees that overlay layers will render correctly.
 */

'use client';

import React, { useRef, useEffect, useState, useCallback } from 'react';
import mapboxgl from 'mapbox-gl';
import { 
  buildSatelliteStyle, 
  buildHybridStyle,
  addFowlDataLayers,
  insertCustomLayer 
} from '@/utils/mapboxStyleBuilder';
import type { Hunt, Location, PrivacyLevel } from '@/lib/types/api';
import 'mapbox-gl/dist/mapbox-gl.css';

const MAPBOX_TOKEN = process.env.NEXT_PUBLIC_MAPBOX_ACCESS_TOKEN;

// CRITICAL: Required for Mapbox GL JS v3+ with react-map-gl v8
if (MAPBOX_TOKEN) {
  mapboxgl.accessToken = MAPBOX_TOKEN;
}

interface ProgrammaticMapProps {
  // Data
  hunts?: Hunt[];
  locations?: Location[];

  // Configuration
  center?: [number, number];
  zoom?: number;
  height?: string;
  useHybridStyle?: boolean; // Use hybrid (satellite + streets) vs pure satellite

  // Callbacks
  onMapClick?: (event: { lng: number; lat: number }) => void;
  onHuntSelect?: (hunt: Hunt | null) => void;
  onLocationSelect?: (location: Location | null) => void;
}

export function ProgrammaticMap({
  hunts = [],
  locations = [],
  center = [-98.5795, 39.8283],
  zoom = 4,
  height = '600px',
  useHybridStyle = true,
  onMapClick,
  onHuntSelect,
  onLocationSelect
}: ProgrammaticMapProps) {
  const mapContainer = useRef<HTMLDivElement>(null);
  const map = useRef<mapboxgl.Map | null>(null);
  const [isLoaded, setIsLoaded] = useState(false);

  // Convert data to GeoJSON
  const huntsGeoJSON = useCallback((): GeoJSON.FeatureCollection => {
    return {
      type: 'FeatureCollection',
      features: hunts.map(hunt => ({
        type: 'Feature' as const,
        geometry: {
          type: 'Point' as const,
          coordinates: [hunt.longitude, hunt.latitude]
        },
        properties: {
          id: hunt.id,
          date: hunt.date,
          privacy_level: hunt.privacy_level || 'private',
          notes: hunt.notes,
          bird_count: hunt.birds?.length || 0,
          weather: hunt.weather_data
        }
      }))
    };
  }, [hunts]);

  const locationsGeoJSON = useCallback((): GeoJSON.FeatureCollection => {
    return {
      type: 'FeatureCollection',
      features: locations.map(location => ({
        type: 'Feature' as const,
        geometry: {
          type: 'Point' as const,
          coordinates: [location.longitude, location.latitude]
        },
        properties: {
          id: location.id,
          name: location.name,
          description: location.description,
          is_owner: location.is_owner,
          shares: location.shares
        }
      }))
    };
  }, [locations]);

  // Initialize map with programmatic style
  useEffect(() => {
    if (!mapContainer.current || map.current) return;

    // Build the style programmatically
    const style = useHybridStyle ? buildHybridStyle() : buildSatelliteStyle();

    // Create the map
    map.current = new mapboxgl.Map({
      container: mapContainer.current,
      style: style,
      center: center,
      zoom: zoom,
      pitch: 0,
      bearing: 0
    });

    // Add navigation controls
    map.current.addControl(new mapboxgl.NavigationControl(), 'top-right');
    map.current.addControl(new mapboxgl.ScaleControl(), 'bottom-left');
    map.current.addControl(new mapboxgl.FullscreenControl(), 'top-right');

    // Handle map load
    map.current.on('load', () => {
      setIsLoaded(true);
      console.log(' Programmatic map loaded successfully');
    });

    // Handle map clicks
    map.current.on('click', (e) => {
      if (onMapClick) {
        onMapClick({ lng: e.lngLat.lng, lat: e.lngLat.lat });
      }
    });

    // Cleanup
    return () => {
      map.current?.remove();
      map.current = null;
    };
  }, []); // Only run once on mount

  // Add data layers when map is loaded
  useEffect(() => {
    if (!map.current || !isLoaded) return;

    // Remove existing layers if they exist
    const layersToRemove = [
      'hunt-clusters',
      'hunt-cluster-count', 
      'hunt-points',
      'location-points'
    ];

    layersToRemove.forEach(id => {
      if (map.current?.getLayer(id)) {
        map.current.removeLayer(id);
      }
    });

    // Remove existing sources
    ['hunts', 'locations'].forEach(id => {
      if (map.current?.getSource(id)) {
        map.current.removeSource(id);
      }
    });

    // Add new sources and layers
    addFowlDataLayers(map.current, huntsGeoJSON(), locationsGeoJSON());

    // Add click handlers for interactive layers
    map.current.on('click', 'hunt-points', (e) => {
      if (!e.features || !e.features[0]) return;

      const properties = e.features[0].properties;
      if (properties && onHuntSelect) {
        // Find the original hunt object
        const hunt = hunts.find(h => h.id === properties.id);
        if (hunt) onHuntSelect(hunt);
      }
    });

    map.current.on('click', 'location-points', (e) => {
      if (!e.features || !e.features[0]) return;

      const properties = e.features[0].properties;
      if (properties && onLocationSelect) {
        // Find the original location object
        const location = locations.find(l => l.id === properties.id);
        if (location) onLocationSelect(location);
      }
    });

    // Change cursor on hover
    map.current.on('mouseenter', 'hunt-points', () => {
      if (map.current) map.current.getCanvas().style.cursor = 'pointer';
    });

    map.current.on('mouseleave', 'hunt-points', () => {
      if (map.current) map.current.getCanvas().style.cursor = '';
    });

    map.current.on('mouseenter', 'location-points', () => {
      if (map.current) map.current.getCanvas().style.cursor = 'pointer';
    });

    map.current.on('mouseleave', 'location-points', () => {
      if (map.current) map.current.getCanvas().style.cursor = '';
    });

  }, [isLoaded, hunts, locations, huntsGeoJSON, locationsGeoJSON, onHuntSelect, onLocationSelect]);

  // Dynamic layer control panel
  const [visibleLayers, setVisibleLayers] = useState({
    hunts: true,
    locations: true,
    clusters: true
  });

  const toggleLayer = useCallback((layerType: keyof typeof visibleLayers) => {
    if (!map.current || !isLoaded) return;

    const newVisibility = !visibleLayers[layerType];

    switch(layerType) {
      case 'hunts':
        map.current.setLayoutProperty('hunt-points', 'visibility', newVisibility ? 'visible' : 'none');
        break;
      case 'locations':
        map.current.setLayoutProperty('location-points', 'visibility', newVisibility ? 'visible' : 'none');
        break;
      case 'clusters':
        map.current.setLayoutProperty('hunt-clusters', 'visibility', newVisibility ? 'visible' : 'none');
        map.current.setLayoutProperty('hunt-cluster-count', 'visibility', newVisibility ? 'visible' : 'none');
        break;
    }

    setVisibleLayers(prev => ({ ...prev, [layerType]: newVisibility }));
  }, [visibleLayers, isLoaded]);

  return (
    <div className="relative w-full" style={{ height }}>
      {/* Map Container */}
      <div ref={mapContainer} className="w-full h-full" />

      {/* Layer Control Panel */}
      <div className="absolute top-4 left-4 bg-white/90 backdrop-blur-sm rounded-lg shadow-lg p-4 space-y-2">
        <h3 className="font-semibold text-sm mb-2">Layers</h3>

        <label className="flex items-center space-x-2 cursor-pointer">
          <input
            type="checkbox"
            checked={visibleLayers.hunts}
            onChange={() => toggleLayer('hunts')}
            className="rounded border-gray-300"
          />
          <span className="text-sm">Hunts</span>
        </label>

        <label className="flex items-center space-x-2 cursor-pointer">
          <input
            type="checkbox"
            checked={visibleLayers.locations}
            onChange={() => toggleLayer('locations')}
            className="rounded border-gray-300"
          />
          <span className="text-sm">Locations</span>
        </label>

        <label className="flex items-center space-x-2 cursor-pointer">
          <input
            type="checkbox"
            checked={visibleLayers.clusters}
            onChange={() => toggleLayer('clusters')}
            className="rounded border-gray-300"
          />
          <span className="text-sm">Clustering</span>
        </label>
      </div>

      {/* Status Indicator */}
      <div className="absolute bottom-4 right-4 bg-white/90 backdrop-blur-sm rounded px-2 py-1 text-xs">
        {isLoaded ? (
          <span className="text-green-600"> Programmatic Style Active</span>
        ) : (
          <span className="text-gray-500">Loading...</span>
        )}
      </div>

      {/* Debug Info */}
      {process.env.NODE_ENV === 'development' && (
        <div className="absolute bottom-12 right-4 bg-black/70 text-white rounded p-2 text-xs font-mono">
          <div>Hunts: {hunts.length}</div>
          <div>Locations: {locations.length}</div>
          <div>Style: {useHybridStyle ? 'Hybrid' : 'Satellite'}</div>
        </div>
      )}
    </div>
  );
}

// Export with default props for easy testing
export default ProgrammaticMap;

