/**
 * Mapbox Performance Optimizer
 * 
 * Production-ready performance optimization utilities for handling
 * multiple large GeoJSON layers in Mapbox GL JS
 */

import mapboxgl from 'mapbox-gl';
import * as turf from '@turf/turf';

// Performance thresholds
export const PERFORMANCE_LIMITS = {
  features: {
    perLayer: 10_000,
    total: 50_000,
    warning: 30_000
  },
  fps: {
    target: 60,
    acceptable: 30,
    critical: 20
  },
  memory: {
    warning: 100_000_000, // 100MB
    critical: 200_000_000 // 200MB
  }
} as const;

/**
 * Viewport-based feature filtering
 * Only loads features visible in current viewport with buffer
 */
export class ViewportFeatureManager {
  private allFeatures = new Map<string, GeoJSON.Feature[]>();
  private viewportFeatures = new Map<string, GeoJSON.Feature[]>();
  private bufferRatio = 0.5; // 50% buffer around viewport
  private lastBounds: mapboxgl.LngLatBounds | null = null;

  /**
   * Register features for a layer
   */
  registerFeatures(layerId: string, features: GeoJSON.Feature[]) {
    this.allFeatures.set(layerId, features);
  }

  /**
   * Update viewport and filter features
   */
  updateViewport(map: mapboxgl.Map): Map<string, GeoJSON.Feature[]> {
    const bounds = map.getBounds();

    // Check if viewport changed significantly (more than 10% difference)
    if (this.lastBounds && this.isSimilarBounds(bounds, this.lastBounds)) {
      return this.viewportFeatures;
    }

    this.lastBounds = bounds;
    const bbox = this.getBufferedBbox(bounds);

    // Filter features for each layer
    this.allFeatures.forEach((features, layerId) => {
      const filtered = this.filterFeaturesInBbox(features, bbox);
      this.viewportFeatures.set(layerId, filtered);
    });

    return this.viewportFeatures;
  }

  /**
   * Get features for a specific layer
   */
  getLayerFeatures(layerId: string): GeoJSON.Feature[] {
    return this.viewportFeatures.get(layerId) || [];
  }

  /**
   * Get statistics about feature counts
   */
  getStats() {
    let totalAll = 0;
    let totalViewport = 0;

    this.allFeatures.forEach(features => totalAll += features.length);
    this.viewportFeatures.forEach(features => totalViewport += features.length);

    return {
      total: totalAll,
      viewport: totalViewport,
      reduction: totalAll > 0 ? ((totalAll - totalViewport) / totalAll * 100).toFixed(1) : 0
    };
  }

  private getBufferedBbox(bounds: mapboxgl.LngLatBounds): [number, number, number, number] {
    const west = bounds.getWest();
    const south = bounds.getSouth();
    const east = bounds.getEast();
    const north = bounds.getNorth();

    const width = east - west;
    const height = north - south;

    return [
      west - width * this.bufferRatio,
      south - height * this.bufferRatio,
      east + width * this.bufferRatio,
      north + height * this.bufferRatio
    ];
  }

  private filterFeaturesInBbox(
    features: GeoJSON.Feature[], 
    bbox: [number, number, number, number]
  ): GeoJSON.Feature[] {
    return features.filter(feature => {
      if (!feature.geometry) return false;

      if (feature.geometry.type === 'Point') {
        const [lng, lat] = feature.geometry.coordinates;
        return lng >= bbox[0] && lng <= bbox[2] && 
               lat >= bbox[1] && lat <= bbox[3];
      }

      // For other geometries, check if bounding box intersects
      try {
        const featureBbox = turf.bbox(feature);
        return this.bboxIntersects(featureBbox as [number, number, number, number], bbox);
      } catch {
        return false;
      }
    });
  }

  private bboxIntersects(
    bbox1: [number, number, number, number], 
    bbox2: [number, number, number, number]
  ): boolean {
    return !(bbox1[2] < bbox2[0] || bbox1[0] > bbox2[2] ||
             bbox1[3] < bbox2[1] || bbox1[1] > bbox2[3]);
  }

  private isSimilarBounds(bounds1: mapboxgl.LngLatBounds, bounds2: mapboxgl.LngLatBounds): boolean {
    const threshold = 0.1; // 10% difference threshold

    const width1 = bounds1.getEast() - bounds1.getWest();
    const width2 = bounds2.getEast() - bounds2.getWest();
    const widthDiff = Math.abs(width1 - width2) / Math.max(width1, width2);

    if (widthDiff > threshold) return false;

    const center1 = bounds1.getCenter();
    const center2 = bounds2.getCenter();
    const centerDiff = Math.abs(center1.lng - center2.lng) + Math.abs(center1.lat - center2.lat);

    return centerDiff < width1 * threshold;
  }
}

/**
 * Level of Detail (LOD) manager
 * Simplifies geometry based on zoom level
 */
export class LODManager {
  private zoomThresholds = {
    high: 14,    // Full detail
    medium: 10,  // Moderate simplification
    low: 6       // Heavy simplification
  };

  /**
   * Get appropriate detail level for zoom
   */
  getDetailLevel(zoom: number): 'high' | 'medium' | 'low' {
    if (zoom >= this.zoomThresholds.high) return 'high';
    if (zoom >= this.zoomThresholds.medium) return 'medium';
    return 'low';
  }

  /**
   * Get simplified features based on zoom
   */
  getOptimizedFeatures(features: GeoJSON.Feature[], zoom: number): GeoJSON.Feature[] {
    const level = this.getDetailLevel(zoom);

    if (level === 'high') {
      return features; // No simplification needed
    }

    // For medium and low detail, reduce feature density
    const sampleRate = level === 'medium' ? 0.5 : 0.25;

    // Sample features for point data
    const sampled = features.filter((_, index) => {
      // Keep all non-point features
      if (feature.geometry.type !== 'Point') return true;
      // Sample points based on rate
      return Math.random() < sampleRate;
    });

    return sampled;
  }

  /**
   * Get cluster configuration for zoom level
   */
  getClusterConfig(zoom: number): { radius: number; maxZoom: number } {
    if (zoom < 5) {
      return { radius: 80, maxZoom: 14 };
    } else if (zoom < 8) {
      return { radius: 60, maxZoom: 14 };
    } else if (zoom < 12) {
      return { radius: 40, maxZoom: 14 };
    }
    return { radius: 20, maxZoom: 16 };
  }
}

/**
 * Layer visibility manager
 * Controls which layers are visible at different zoom levels
 */
export class LayerVisibilityManager {
  private layerConfigs = new Map<string, { minZoom: number; maxZoom: number }>();
  private lastZoom = -1;

  /**
   * Register a layer with zoom constraints
   */
  registerLayer(layerId: string, minZoom = 0, maxZoom = 22) {
    this.layerConfigs.set(layerId, { minZoom, maxZoom });
  }

  /**
   * Update visibility based on current zoom
   */
  updateVisibility(map: mapboxgl.Map): string[] {
    const zoom = map.getZoom();

    // Only update if zoom changed significantly
    if (Math.abs(zoom - this.lastZoom) < 0.1) {
      return [];
    }

    this.lastZoom = zoom;
    const visibleLayers: string[] = [];

    this.layerConfigs.forEach((config, layerId) => {
      if (!map.getLayer(layerId)) return;

      const shouldBeVisible = zoom >= config.minZoom && zoom <= config.maxZoom;
      const currentVisibility = map.getLayoutProperty(layerId, 'visibility');
      const isVisible = currentVisibility !== 'none';

      if (shouldBeVisible !== isVisible) {
        map.setLayoutProperty(
          layerId, 
          'visibility', 
          shouldBeVisible ? 'visible' : 'none'
        );
      }

      if (shouldBeVisible) {
        visibleLayers.push(layerId);
      }
    });

    return visibleLayers;
  }
}

/**
 * Performance monitor
 * Tracks FPS, memory usage, and feature counts
 */
export class PerformanceMonitor {
  private stats = {
    fps: 60,
    renderTime: 0,
    memoryUsage: 0,
    featureCount: 0,
    layerCount: 0,
    visibleLayers: 0
  };

  private frameCount = 0;
  private lastTime = performance.now();
  private callbacks: Array<(stats: typeof this.stats) => void> = [];

  /**
   * Start monitoring performance
   */
  startMonitoring(map: mapboxgl.Map) {
    const measureFrame = () => {
      const now = performance.now();
      const delta = now - this.lastTime;

      this.frameCount++;

      // Update stats every second
      if (delta >= 1000) {
        this.stats.fps = Math.round((this.frameCount * 1000) / delta);
        this.frameCount = 0;
        this.lastTime = now;

        // Measure memory if available
        if ('memory' in performance) {
          this.stats.memoryUsage = (performance as any).memory.usedJSHeapSize;
        }

        // Count layers
        const style = map.getStyle();
        if (style) {
          this.stats.layerCount = style.layers.length;
          this.stats.visibleLayers = style.layers.filter(
            layer => map.getLayoutProperty(layer.id, 'visibility') !== 'none'
          ).length;
        }

        // Emit stats
        this.emitStats();
      }

      requestAnimationFrame(measureFrame);
    };

    requestAnimationFrame(measureFrame);
  }

  /**
   * Subscribe to stats updates
   */
  onStatsUpdate(callback: (stats: typeof this.stats) => void) {
    this.callbacks.push(callback);
  }

  /**
   * Get current stats
   */
  getStats() {
    return { ...this.stats };
  }

  /**
   * Check if performance is degraded
   */
  isPerformanceDegraded(): boolean {
    return this.stats.fps < PERFORMANCE_LIMITS.fps.acceptable ||
           this.stats.memoryUsage > PERFORMANCE_LIMITS.memory.warning;
  }

  private emitStats() {
    // Log warnings
    if (this.stats.fps < PERFORMANCE_LIMITS.fps.critical) {
      console.warn(` Critical: FPS dropped to ${this.stats.fps}`);
    } else if (this.stats.fps < PERFORMANCE_LIMITS.fps.acceptable) {
      console.warn(` Warning: Low FPS: ${this.stats.fps}`);
    }

    if (this.stats.memoryUsage > PERFORMANCE_LIMITS.memory.critical) {
      console.warn(` Critical: Memory usage at ${(this.stats.memoryUsage / 1_000_000).toFixed(2)}MB`);
    }

    // Call callbacks
    this.callbacks.forEach(cb => cb(this.stats));
  }
}

/**
 * Automatic quality manager
 * Adjusts rendering quality based on performance
 */
export class QualityManager {
  private qualityLevel: 'high' | 'medium' | 'low' = 'high';
  private performanceHistory: number[] = [];
  private autoAdjust = true;

  /**
   * Enable/disable automatic quality adjustment
   */
  setAutoAdjust(enabled: boolean) {
    this.autoAdjust = enabled;
  }

  /**
   * Manually set quality level
   */
  setQuality(level: 'high' | 'medium' | 'low', map: mapboxgl.Map) {
    this.qualityLevel = level;
    this.applyQualitySettings(map);
  }

  /**
   * Check and adjust quality based on FPS
   */
  checkPerformance(fps: number, map: mapboxgl.Map) {
    if (!this.autoAdjust) return;

    this.performanceHistory.push(fps);

    // Keep last 10 measurements
    if (this.performanceHistory.length > 10) {
      this.performanceHistory.shift();
    }

    // Need at least 5 measurements
    if (this.performanceHistory.length < 5) return;

    const avgFPS = this.performanceHistory.reduce((a, b) => a + b, 0) / 
                   this.performanceHistory.length;

    // Determine needed quality level
    let targetLevel: 'high' | 'medium' | 'low' = 'high';

    if (avgFPS < PERFORMANCE_LIMITS.fps.critical) {
      targetLevel = 'low';
    } else if (avgFPS < PERFORMANCE_LIMITS.fps.acceptable) {
      targetLevel = 'medium';
    }

    // Apply if changed
    if (targetLevel !== this.qualityLevel) {
      console.log(` Auto-adjusting quality from ${this.qualityLevel} to ${targetLevel} (avg FPS: ${avgFPS.toFixed(1)})`);
      this.setQuality(targetLevel, map);
    }
  }

  /**
   * Get current quality level
   */
  getQualityLevel() {
    return this.qualityLevel;
  }

  private applyQualitySettings(map: mapboxgl.Map) {
    const style = map.getStyle();
    if (!style) return;

    switch (this.qualityLevel) {
      case 'low':
        // Aggressive optimizations
        this.setLayerQuality(map, {
          circleBlur: 0,
          lineBlur: 0,
          symbolSpacing: 500,
          textOptional: true
        });
        break;

      case 'medium':
        // Balanced settings
        this.setLayerQuality(map, {
          circleBlur: 0.5,
          lineBlur: 0.5,
          symbolSpacing: 250,
          textOptional: false
        });
        break;

      case 'high':
        // Full quality
        this.setLayerQuality(map, {
          circleBlur: 1,
          lineBlur: 1,
          symbolSpacing: 100,
          textOptional: false
        });
        break;
    }
  }

  private setLayerQuality(map: mapboxgl.Map, settings: any) {
    const style = map.getStyle();
    if (!style) return;

    style.layers.forEach(layer => {
      // Adjust paint properties based on layer type
      if (layer.type === 'circle' && settings.circleBlur !== undefined) {
        map.setPaintProperty(layer.id, 'circle-blur', settings.circleBlur);
      }

      if (layer.type === 'line' && settings.lineBlur !== undefined) {
        map.setPaintProperty(layer.id, 'line-blur', settings.lineBlur);
      }

      if (layer.type === 'symbol' && settings.textOptional !== undefined) {
        map.setLayoutProperty(layer.id, 'text-optional', settings.textOptional);
      }
    });
  }
}

/**
 * Comprehensive performance optimizer
 * Combines all optimization strategies
 */
export class MapPerformanceOptimizer {
  private viewportManager = new ViewportFeatureManager();
  private lodManager = new LODManager();
  private visibilityManager = new LayerVisibilityManager();
  private performanceMonitor = new PerformanceMonitor();
  private qualityManager = new QualityManager();

  private map: mapboxgl.Map | null = null;
  private updateTimer: NodeJS.Timeout | null = null;

  /**
   * Initialize optimizer with map instance
   */
  initialize(map: mapboxgl.Map) {
    this.map = map;

    // Start performance monitoring
    this.performanceMonitor.startMonitoring(map);

    // Subscribe to performance updates
    this.performanceMonitor.onStatsUpdate(stats => {
      this.qualityManager.checkPerformance(stats.fps, map);
    });

    // Update on map movements
    map.on('moveend', () => this.scheduleUpdate());
    map.on('zoomend', () => this.scheduleUpdate());
  }

  /**
   * Register features for optimization
   */
  registerFeatures(layerId: string, features: GeoJSON.Feature[]) {
    this.viewportManager.registerFeatures(layerId, features);
  }

  /**
   * Register layer for visibility management
   */
  registerLayer(layerId: string, minZoom = 0, maxZoom = 22) {
    this.visibilityManager.registerLayer(layerId, minZoom, maxZoom);
  }

  /**
   * Get optimized features for current viewport and zoom
   */
  getOptimizedFeatures(layerId: string): GeoJSON.Feature[] {
    if (!this.map) return [];

    const zoom = this.map.getZoom();
    const viewportFeatures = this.viewportManager.getLayerFeatures(layerId);
    const optimizedFeatures = this.lodManager.getOptimizedFeatures(viewportFeatures, zoom);

    return optimizedFeatures;
  }

  /**
   * Get cluster configuration for current zoom
   */
  getClusterConfig(): { radius: number; maxZoom: number } {
    if (!this.map) return { radius: 50, maxZoom: 14 };
    return this.lodManager.getClusterConfig(this.map.getZoom());
  }

  /**
   * Get performance stats
   */
  getStats() {
    return {
      performance: this.performanceMonitor.getStats(),
      viewport: this.viewportManager.getStats(),
      quality: this.qualityManager.getQualityLevel()
    };
  }

  /**
   * Set quality mode
   */
  setQualityMode(mode: 'auto' | 'high' | 'medium' | 'low') {
    if (!this.map) return;

    if (mode === 'auto') {
      this.qualityManager.setAutoAdjust(true);
    } else {
      this.qualityManager.setAutoAdjust(false);
      this.qualityManager.setQuality(mode, this.map);
    }
  }

  /**
   * Clean up resources
   */
  destroy() {
    if (this.updateTimer) {
      clearTimeout(this.updateTimer);
    }
    this.map = null;
  }

  private scheduleUpdate() {
    if (this.updateTimer) {
      clearTimeout(this.updateTimer);
    }

    // Debounce updates
    this.updateTimer = setTimeout(() => {
      this.update();
    }, 100);
  }

  private update() {
    if (!this.map) return;

    // Update viewport filtering
    this.viewportManager.updateViewport(this.map);

    // Update layer visibility
    this.visibilityManager.updateVisibility(this.map);
  }
}

// Export singleton instance for easy use
export const performanceOptimizer = new MapPerformanceOptimizer();

