/**
 * OptimizedProgrammaticMap Component
 * 
 * High-performance map implementation with optimization strategies
 * for handling up to 16 large GeoJSON layers simultaneously.
 * 
 * Optimizations include:
 * - Viewport-based feature filtering
 * - Level of Detail (LOD) management  
 * - Smart clustering
 * - Automatic quality adjustment
 * - Performance monitoring
 */

'use client';

import React, { useRef, useEffect, useState, useCallback, useMemo } from 'react';
import mapboxgl from 'mapbox-gl';
import { 
  MapPerformanceOptimizer,
  ViewportFeatureManager,
  PerformanceMonitor,
  PERFORMANCE_LIMITS
} from '@/utils/mapboxPerformanceOptimizer';
import { 
  buildSatelliteStyle, 
  buildHybridStyle 
} from '@/utils/mapboxStyleBuilder';
import type { Hunt, Location, PrivacyLevel, DataType } from '@/lib/types/api';
import 'mapbox-gl/dist/mapbox-gl.css';

const MAPBOX_TOKEN = process.env.NEXT_PUBLIC_MAPBOX_ACCESS_TOKEN;

// CRITICAL: Required for Mapbox GL JS v3+
if (MAPBOX_TOKEN) {
  mapboxgl.accessToken = MAPBOX_TOKEN;
}

interface OptimizedMapProps {
  // Data sources (can be large arrays)
  hunts?: Hunt[];
  locations?: Location[];

  // Layer configuration
  layers?: {
    id: string;
    type: DataType;
    privacyLevel: PrivacyLevel;
    data: GeoJSON.FeatureCollection;
    minZoom?: number;
    maxZoom?: number;
  }[];

  // Map configuration
  center?: [number, number];
  zoom?: number;
  height?: string;
  useHybridStyle?: boolean;

  // Performance settings
  performanceMode?: 'auto' | 'high' | 'medium' | 'low';
  maxFeaturesPerLayer?: number;
  enableClustering?: boolean;

  // Callbacks
  onMapClick?: (event: { lng: number; lat: number }) => void;
  onFeatureClick?: (feature: GeoJSON.Feature) => void;
  onPerformanceChange?: (stats: any) => void;
}

// Performance dashboard component
function PerformanceDashboard({ stats }: { stats: any }) {
  const getFPSColor = (fps: number) => {
    if (fps >= 50) return 'text-green-500';
    if (fps >= 30) return 'text-yellow-500';
    return 'text-red-500';
  };

  const getMemoryColor = (mb: number) => {
    if (mb < 100) return 'text-green-500';
    if (mb < 200) return 'text-yellow-500';
    return 'text-red-500';
  };

  if (!stats?.performance) return null;

  const memoryMB = Math.round(stats.performance.memoryUsage / 1_000_000);

  return (
    <div className="absolute top-20 right-4 bg-black/80 text-white rounded-lg shadow-lg p-3 text-xs font-mono">
      <div className="font-bold mb-2 text-white/90">Performance Monitor</div>

      <div className="space-y-1">
        <div className="flex justify-between items-center">
          <span className="text-white/70">FPS:</span>
          <span className={`font-bold ${getFPSColor(stats.performance.fps)}`}>
            {stats.performance.fps}
          </span>
        </div>

        <div className="flex justify-between items-center">
          <span className="text-white/70">Memory:</span>
          <span className={`font-bold ${getMemoryColor(memoryMB)}`}>
            {memoryMB}MB
          </span>
        </div>

        <div className="flex justify-between items-center">
          <span className="text-white/70">Features:</span>
          <span className="text-white">
            {stats.viewport?.viewport?.toLocaleString() || 0} / {stats.viewport?.total?.toLocaleString() || 0}
          </span>
        </div>

        <div className="flex justify-between items-center">
          <span className="text-white/70">Reduction:</span>
          <span className="text-green-500 font-bold">
            {stats.viewport?.reduction || 0}%
          </span>
        </div>

        <div className="flex justify-between items-center">
          <span className="text-white/70">Quality:</span>
          <span className="text-white uppercase font-bold">
            {stats.quality || 'high'}
          </span>
        </div>

        <div className="flex justify-between items-center">
          <span className="text-white/70">Layers:</span>
          <span className="text-white">
            {stats.performance.visibleLayers} / {stats.performance.layerCount}
          </span>
        </div>
      </div>

      {stats.performance.fps < 30 && (
        <div className="mt-2 pt-2 border-t border-red-500/50">
          <div className="text-red-400 text-xs">
             Performance degraded
          </div>
          <div className="text-white/60 text-xs mt-1">
            Quality auto-adjusting...
          </div>
        </div>
      )}
    </div>
  );
}

export function OptimizedProgrammaticMap({
  hunts = [],
  locations = [],
  layers = [],
  center = [-98.5795, 39.8283],
  zoom = 4,
  height = '600px',
  useHybridStyle = true,
  performanceMode = 'auto',
  maxFeaturesPerLayer = PERFORMANCE_LIMITS.features.perLayer,
  enableClustering = true,
  onMapClick,
  onFeatureClick,
  onPerformanceChange
}: OptimizedMapProps) {
  const mapContainer = useRef<HTMLDivElement>(null);
  const map = useRef<mapboxgl.Map | null>(null);
  const optimizer = useRef<MapPerformanceOptimizer>(new MapPerformanceOptimizer());

  const [isLoaded, setIsLoaded] = useState(false);
  const [performanceStats, setPerformanceStats] = useState<any>(null);
  const [showPerformance, setShowPerformance] = useState(false);

  // Convert hunts to GeoJSON with performance optimization
  const huntsGeoJSON = useMemo((): GeoJSON.FeatureCollection => {
    // Limit features if needed
    const limitedHunts = hunts.slice(0, maxFeaturesPerLayer);

    if (hunts.length > maxFeaturesPerLayer) {
      console.warn(` Limiting hunts from ${hunts.length} to ${maxFeaturesPerLayer} for performance`);
    }

    return {
      type: 'FeatureCollection',
      features: limitedHunts.map(hunt => ({
        type: 'Feature' as const,
        geometry: {
          type: 'Point' as const,
          coordinates: [hunt.longitude, hunt.latitude]
        },
        properties: {
          id: hunt.id,
          date: hunt.date,
          privacy_level: hunt.privacy_level || 'private',
          bird_count: hunt.birds?.length || 0,
          // Only include essential properties to reduce memory
          cluster: enableClustering
        }
      }))
    };
  }, [hunts, maxFeaturesPerLayer, enableClustering]);

  // Convert locations to GeoJSON with optimization
  const locationsGeoJSON = useMemo((): GeoJSON.FeatureCollection => {
    const limitedLocations = locations.slice(0, maxFeaturesPerLayer);

    if (locations.length > maxFeaturesPerLayer) {
      console.warn(` Limiting locations from ${locations.length} to ${maxFeaturesPerLayer} for performance`);
    }

    return {
      type: 'FeatureCollection',
      features: limitedLocations.map(location => ({
        type: 'Feature' as const,
        geometry: {
          type: 'Point' as const,
          coordinates: [location.longitude, location.latitude]
        },
        properties: {
          id: location.id,
          name: location.name,
          is_owner: location.is_owner
        }
      }))
    };
  }, [locations, maxFeaturesPerLayer]);

  // Initialize map with optimizations
  useEffect(() => {
    if (!mapContainer.current || map.current) return;

    // Build optimized style
    const style = useHybridStyle ? buildHybridStyle() : buildSatelliteStyle();

    // Create map with performance settings
    map.current = new mapboxgl.Map({
      container: mapContainer.current,
      style: style,
      center: center,
      zoom: zoom,
      pitch: 0,
      bearing: 0,
      // Performance optimizations
      antialias: performanceMode === 'high', // Only use antialiasing in high quality mode
      preserveDrawingBuffer: false,
      refreshExpiredTiles: false,
      maxTileCacheSize: performanceMode === 'low' ? 50 : 100,
      trackResize: true
    });

    // Add controls
    map.current.addControl(new mapboxgl.NavigationControl(), 'top-right');
    map.current.addControl(new mapboxgl.ScaleControl(), 'bottom-left');

    // Initialize performance optimizer
    optimizer.current.initialize(map.current);
    optimizer.current.setQualityMode(performanceMode);

    // Handle map load
    map.current.on('load', () => {
      setIsLoaded(true);
      console.log(' Optimized map loaded');
    });

    // Handle clicks
    map.current.on('click', (e) => {
      if (onMapClick) {
        onMapClick({ lng: e.lngLat.lng, lat: e.lngLat.lat });
      }
    });

    // Performance monitoring
    const performanceTimer = setInterval(() => {
      const stats = optimizer.current.getStats();
      setPerformanceStats(stats);

      if (onPerformanceChange) {
        onPerformanceChange(stats);
      }
    }, 1000);

    // Cleanup
    return () => {
      clearInterval(performanceTimer);
      optimizer.current.destroy();
      map.current?.remove();
      map.current = null;
    };
  }, []); // Only run once

  // Add optimized layers when map loads
  useEffect(() => {
    if (!map.current || !isLoaded) return;

    // Clean up existing layers
    const layersToRemove = [
      'hunt-clusters',
      'hunt-cluster-count', 
      'hunt-points',
      'location-points'
    ];

    layersToRemove.forEach(id => {
      if (map.current?.getLayer(id)) {
        map.current.removeLayer(id);
      }
    });

    ['hunts', 'locations'].forEach(id => {
      if (map.current?.getSource(id)) {
        map.current.removeSource(id);
      }
    });

    // Register features with optimizer
    optimizer.current.registerFeatures('hunts', huntsGeoJSON.features);
    optimizer.current.registerFeatures('locations', locationsGeoJSON.features);

    // Add hunt source with clustering
    if (enableClustering && huntsGeoJSON.features.length > 100) {
      const clusterConfig = optimizer.current.getClusterConfig();

      map.current.addSource('hunts', {
        type: 'geojson',
        data: huntsGeoJSON,
        cluster: true,
        clusterMaxZoom: clusterConfig.maxZoom,
        clusterRadius: clusterConfig.radius,
        clusterProperties: {
          total_birds: ['+', ['get', 'bird_count']]
        }
      });

      // Add cluster layer
      map.current.addLayer({
        id: 'hunt-clusters',
        type: 'circle',
        source: 'hunts',
        filter: ['has', 'point_count'],
        paint: {
          'circle-color': [
            'step',
            ['get', 'point_count'],
            '#51bbd6', 10,
            '#f1f075', 50,
            '#f28cb1'
          ],
          'circle-radius': [
            'step',
            ['get', 'point_count'],
            15, 10,
            20, 50,
            25
          ],
          'circle-opacity': 0.8
        }
      });

      // Add cluster count
      map.current.addLayer({
        id: 'hunt-cluster-count',
        type: 'symbol',
        source: 'hunts',
        filter: ['has', 'point_count'],
        layout: {
          'text-field': '{point_count_abbreviated}',
          'text-font': ['DIN Offc Pro Medium', 'Arial Unicode MS Bold'],
          'text-size': 12
        },
        paint: {
          'text-color': '#fff'
        }
      });

      // Register cluster layers
      optimizer.current.registerLayer('hunt-clusters', 0, 14);
      optimizer.current.registerLayer('hunt-cluster-count', 0, 14);
    } else {
      // Non-clustered source
      map.current.addSource('hunts', {
        type: 'geojson',
        data: huntsGeoJSON
      });
    }

    // Add hunt points layer
    map.current.addLayer({
      id: 'hunt-points',
      type: 'circle',
      source: 'hunts',
      filter: enableClustering ? ['!', ['has', 'point_count']] : true,
      paint: {
        'circle-radius': [
          'interpolate', ['linear'], ['zoom'],
          5, 3,
          10, 5,
          15, 8
        ],
        'circle-color': [
          'case',
          ['==', ['get', 'privacy_level'], 'public'], '#10b981',
          ['==', ['get', 'privacy_level'], 'friends'], '#3b82f6',
          ['==', ['get', 'privacy_level'], 'private'], '#ef4444',
          '#6b7280'
        ],
        'circle-stroke-width': 2,
        'circle-stroke-color': '#fff',
        'circle-opacity': [
          'interpolate', ['linear'], ['zoom'],
          5, 0.5,
          10, 0.7,
          15, 0.9
        ]
      }
    });

    optimizer.current.registerLayer('hunt-points', enableClustering ? 10 : 0, 22);

    // Add locations source and layer
    map.current.addSource('locations', {
      type: 'geojson',
      data: locationsGeoJSON
    });

    map.current.addLayer({
      id: 'location-points',
      type: 'symbol',
      source: 'locations',
      layout: {
        'icon-image': 'marker-15',
        'icon-size': [
          'interpolate', ['linear'], ['zoom'],
          5, 0.5,
          10, 0.8,
          15, 1.2
        ],
        'text-field': ['get', 'name'],
        'text-font': ['Open Sans Regular', 'Arial Unicode MS Regular'],
        'text-offset': [0, 1.5],
        'text-anchor': 'top',
        'text-size': 12,
        'text-optional': true // Don't force text rendering at low zoom
      },
      paint: {
        'text-color': '#333',
        'text-halo-color': '#fff',
        'text-halo-width': 2
      }
    });

    optimizer.current.registerLayer('location-points', 8, 22);

    // Add custom layers if provided
    layers.forEach((layer, index) => {
      const layerId = layer.id || `custom-layer-${index}`;

      // Register with optimizer
      optimizer.current.registerFeatures(layerId, layer.data.features);
      optimizer.current.registerLayer(layerId, layer.minZoom || 0, layer.maxZoom || 22);

      // Add source
      map.current!.addSource(layerId, {
        type: 'geojson',
        data: layer.data
      });

      // Add layer with data-driven styling
      map.current!.addLayer({
        id: layerId,
        type: 'circle',
        source: layerId,
        paint: {
          'circle-radius': 6,
          'circle-color': getColorForPrivacyLevel(layer.privacyLevel),
          'circle-opacity': 0.8
        }
      });
    });

    // Add interaction handlers
    ['hunt-points', 'location-points'].forEach(layerId => {
      map.current!.on('click', layerId, (e) => {
        if (e.features && e.features[0] && onFeatureClick) {
          onFeatureClick(e.features[0]);
        }
      });

      map.current!.on('mouseenter', layerId, () => {
        if (map.current) map.current.getCanvas().style.cursor = 'pointer';
      });

      map.current!.on('mouseleave', layerId, () => {
        if (map.current) map.current.getCanvas().style.cursor = '';
      });
    });

    // Dynamic zoom-based optimization
    const handleZoomChange = () => {
      if (!map.current) return;

      const zoom = map.current.getZoom();
      const clusterConfig = optimizer.current.getClusterConfig();

      // Update cluster radius dynamically
      const source = map.current.getSource('hunts') as mapboxgl.GeoJSONSource;
      if (source && source.type === 'geojson') {
        // Note: setClusterOptions might not be available in all versions
        // This is a demonstration of the concept
        console.log(`Zoom: ${zoom.toFixed(1)}, Cluster radius: ${clusterConfig.radius}`);
      }
    };

    map.current.on('zoom', handleZoomChange);

    return () => {
      map.current?.off('zoom', handleZoomChange);
    };

  }, [isLoaded, huntsGeoJSON, locationsGeoJSON, layers, enableClustering, onFeatureClick]);

  // Helper function for privacy colors
  const getColorForPrivacyLevel = (level: PrivacyLevel): string => {
    switch (level) {
      case 'public': return '#10b981';
      case 'friends': return '#3b82f6';
      case 'private': return '#ef4444';
      default: return '#6b7280';
    }
  };

  return (
    <div className="relative w-full" style={{ height }}>
      {/* Map Container */}
      <div ref={mapContainer} className="w-full h-full" />

      {/* Performance Controls */}
      <div className="absolute top-4 left-4 bg-white/90 backdrop-blur-sm rounded-lg shadow-lg p-3">
        <div className="flex items-center space-x-3">
          <button
            onClick={() => setShowPerformance(!showPerformance)}
            className="px-3 py-1 bg-gray-800 text-white rounded text-sm hover:bg-gray-700 transition-colors"
          >
            {showPerformance ? 'Hide' : 'Show'} Performance
          </button>

          <select
            value={performanceMode}
            onChange={(e) => {
              const mode = e.target.value as any;
              optimizer.current.setQualityMode(mode);
            }}
            className="px-2 py-1 border rounded text-sm"
          >
            <option value="auto">Auto Quality</option>
            <option value="high">High Quality</option>
            <option value="medium">Medium Quality</option>
            <option value="low">Low Quality</option>
          </select>
        </div>
      </div>

      {/* Performance Dashboard */}
      {showPerformance && performanceStats && (
        <PerformanceDashboard stats={performanceStats} />
      )}

      {/* Status Indicator */}
      <div className="absolute bottom-4 right-4 bg-white/90 backdrop-blur-sm rounded px-3 py-1 text-xs">
        {isLoaded ? (
          <div className="flex items-center space-x-2">
            <span className="text-green-600"></span>
            <span>Optimized Rendering Active</span>
            {enableClustering && (
              <span className="text-gray-500"> Clustering Enabled</span>
            )}
          </div>
        ) : (
          <span className="text-gray-500">Initializing...</span>
        )}
      </div>

      {/* Debug Info */}
      {process.env.NODE_ENV === 'development' && (
        <div className="absolute bottom-12 left-4 bg-black/70 text-white rounded p-2 text-xs font-mono space-y-1">
          <div>Total Hunts: {hunts.length}</div>
          <div>Total Locations: {locations.length}</div>
          <div>Custom Layers: {layers.length}</div>
          <div>Max Features/Layer: {maxFeaturesPerLayer}</div>
          <div>Performance Mode: {performanceMode}</div>
          {performanceStats && (
            <>
              <div className="border-t border-white/20 pt-1 mt-1">
                Viewport Features: {performanceStats.viewport?.viewport || 0}
              </div>
              <div>Feature Reduction: {performanceStats.viewport?.reduction || 0}%</div>
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default OptimizedProgrammaticMap;

