/**
 * OptimizedProgrammaticMap Component
 * 
 * High-performance map implementation with optimization strategies
 * for handling up to 16 large GeoJSON layers simultaneously.
 * 
 * Optimizations include:
 * - Viewport-based feature filtering
 * - Level of Detail (LOD) management  
 * - Smart clustering
 * - Automatic quality adjustment
 * - Performance monitoring
 */

'use client';

import React, { useRef, useEffect, useState, useCallback, useMemo } from 'react';
import mapboxgl from 'mapbox-gl';
import { 
  MapPerformanceOptimizer,
  ViewportFeatureManager,
  PerformanceMonitor,
  PERFORMANCE_LIMITS
} from '@/utils/mapboxPerformanceOptimizer';
import { 
  buildSatelliteStyle, 
  buildHybridStyle 
} from '@/utils/mapboxStyleBuilder';
import type { Hunt, Location, PrivacyLevel, DataType } from '@/lib/types/api';
import 'mapbox-gl/dist/mapbox-gl.css';

const MAPBOX_TOKEN = process.env.NEXT_PUBLIC_MAPBOX_ACCESS_TOKEN;

// CRITICAL: Required for Mapbox GL JS v3+
if (MAPBOX_TOKEN) {
  mapboxgl.accessToken = MAPBOX_TOKEN;
}

interface OptimizedMapProps {
  // Data sources (can be large arrays)
  hunts?: Hunt[];
  locations?: Location[];

  // Layer configuration
  layers?: {
    id: string;
    type: DataType;
    privacyLevel: PrivacyLevel;
    data: GeoJSON.FeatureCollection;
    minZoom?: number;
    maxZoom?: number;
  }[];

  // Map configuration
  center?: [number, number];
  zoom?: number;
  height?: string;
  useHybridStyle?: boolean;

  // Performance settings
  performanceMode?: 'auto' | 'high' | 'medium' | 'low';
  maxFeaturesPerLayer?: number;
  enableClustering?: boolean;

  // Callbacks
  onMapClick?: (event: { lng: number; lat: number }) => void;
  onFeatureClick?: (feature: GeoJSON.Feature) => void;
  onPerformanceChange?: (stats: any) => void;
}

// Performance dashboard component
function PerformanceDashboard({ stats }: { stats: any }) {
  const getFPSColor = (fps: number) => {
    if (fps >= 50) return 'text-green-500';
    if (fps >= 30) return 'text-yellow-500';
    return 'text-red-500';
  };

  const getMemoryColor = (mb: number) => {
    if (mb < 100) return 'text-green-500';
    if (mb < 200) return 'text-yellow-500';
    return 'text-red-500';
  };

  if (!stats?.performance) return null;

  const memoryMB = Math.round(stats.performance.memoryUsage / 1_000_000);

  return (
    <div className="absolute top-20 right-4 bg-black/80 text-white rounded-lg shadow-lg p-3 text-xs font-mono">
      <div className="font-bold mb-2 text-white/90">Performance Monitor</div>

      <div className="space-y-1">
        <div className="flex justify-between items-center">
          <span className="text-white/70">FPS:</span>
          <span className={`font-bold ${getFPSColor(stats.performance.fps)}`}>
            {stats.performance.fps}
          </span>
        </div>

        <div className="flex justify-between items-center">
          <span className="text-white/70">Memory:</span>
          <span className={`font-bold ${getMemoryColor(memoryMB)}`}>
            {memoryMB}MB
          </span>
        </div>

        <div className="flex justify-between items-center">
          <span className="text-white/70">Features:</span>
          <span className="text-white">
            {stats.viewport?.viewport?.toLocaleString() || 0} / {stats.viewport?.total?.toLocaleString() || 0}
          </span>
        </div>

        <div className="flex justify-between items-center">
          <span className="text-white/70">Reduction:</span>
          <span className="text-green-500 font-bold">
            {stats.viewport?.reduction || 0}%
          </span>
        </div>

        <div className="flex justify-between items-center">
          <span className="text-white/70">Quality:</span>
          <span className="text-white uppercase font-bold">
            {stats.quality || 'high'}
          </span>
        </div>

        <div className="flex justify-between items-center">
          <span className="text-white/70">Layers:</span>
          <span className="text-white">
            {stats.performance.visibleLayers} / {stats.performance.layerCount}
          </span>
        </div>
      </div>

      {stats.performance.fps < 30 && (
        <div className="mt-2 pt-2 border-t border-red-500/50">
          <div className="text-red-400 text-xs">
             Performance degraded
          </div>
          <div className="text-white/60 text-xs mt-1">
            Quality auto-adjusting...
          </div>
        </div>
      )}
    </div>
  );
}

export function OptimizedProgrammaticMap({
  hunts = [],
  locations = [],
  layers = [],
  center = [-98.5795, 39.8283],
  zoom = 4,
  height = '600px',
  useHybridStyle = true,
  performanceMode = 'auto',
  maxFeaturesPerLayer = PERFORMANCE_LIMITS.features.perLayer,
  enableClustering = true,
  onMapClick,
  onFeatureClick,
  onPerformanceChange
}: OptimizedMapProps) {
  const mapContainer = useRef<HTMLDivElement>(null);
  const map = useRef<mapboxgl.Map | null>(null);
  const optimizer = useRef<MapPerformanceOptimizer>(new MapPerformanceOptimizer());

  const [isLoaded, setIsLoaded] = useState(false);
  const [performanceStats, setPerformanceStats] = useState<any>(null);
  const [showPerformance, setShowPerformance] = useState(false);

  // Convert hunts to GeoJSON with performance optimization
  const huntsGeoJSON = useMemo((): GeoJSON.FeatureCollection => {
    // Limit features if needed
    const limitedHunts = hunts.slice(0, maxFeaturesPerLayer);

    if (hunts.length > maxFeaturesPerLayer) {
      console.warn(` Limiting hunts from ${hunts.length} to ${maxFeaturesPerLayer} for performance`);
    }

    return {
      type: 'FeatureCollection',
      features: limitedHunts.map(hunt => ({
        type: 'Feature' as const,
        geometry: {
          type: 'Point' as const,
          coordinates: [hunt.longitude, hunt.latitude]
        },
        properties: {
          id: hunt.id,
          date: hunt.date,
          privacy_level: hunt.privacy_level || 'private',
          bird_count: hunt.birds?.length || 0,
          // Only include essential properties to reduce memory
          cluster: enableClustering
