  // Convert locations to GeoJSON with optimization
  const locationsGeoJSON = useMemo((): GeoJSON.FeatureCollection => {
    const limitedLocations = locations.slice(0, maxFeaturesPerLayer);

    if (locations.length > maxFeaturesPerLayer) {
      console.warn(` Limiting locations from ${locations.length} to ${maxFeaturesPerLayer} for performance`);
    }

    return {
      type: 'FeatureCollection',
      features: limitedLocations.map(location => ({
        type: 'Feature' as const,
        geometry: {
          type: 'Point' as const,
          coordinates: [location.longitude, location.latitude]
        },
        properties: {
          id: location.id,
          name: location.name,
          is_owner: location.is_owner
        }
      }))
    };
  }, [locations, maxFeaturesPerLayer]);

  // Initialize map with optimizations
  useEffect(() => {
    if (!mapContainer.current || map.current) return;

    // Build optimized style
    const style = useHybridStyle ? buildHybridStyle() : buildSatelliteStyle();

    // Create map with performance settings
    map.current = new mapboxgl.Map({
      container: mapContainer.current,
      style: style,
      center: center,
      zoom: zoom,
      pitch: 0,
      bearing: 0,
      // Performance optimizations
      antialias: performanceMode === 'high', // Only use antialiasing in high quality mode
      preserveDrawingBuffer: false,
      refreshExpiredTiles: false,
      maxTileCacheSize: performanceMode === 'low' ? 50 : 100,
      trackResize: true
    });

    // Add controls
    map.current.addControl(new mapboxgl.NavigationControl(), 'top-right');
    map.current.addControl(new mapboxgl.ScaleControl(), 'bottom-left');

    // Initialize performance optimizer
    optimizer.current.initialize(map.current);
    optimizer.current.setQualityMode(performanceMode);

    // Handle map load
    map.current.on('load', () => {
      setIsLoaded(true);
      console.log(' Optimized map loaded');
    });

    // Handle clicks
    map.current.on('click', (e) => {
      if (onMapClick) {
        onMapClick({ lng: e.lngLat.lng, lat: e.lngLat.lat });
      }
    });

    // Performance monitoring
    const performanceTimer = setInterval(() => {
      const stats = optimizer.current.getStats();
      setPerformanceStats(stats);

      if (onPerformanceChange) {
        onPerformanceChange(stats);
      }
    }, 1000);

    // Cleanup
    return () => {
      clearInterval(performanceTimer);
      optimizer.current.destroy();
      map.current?.remove();
      map.current = null;
    };
  }, []); // Only run once

  // Add optimized layers when map loads
  useEffect(() => {
    if (!map.current || !isLoaded) return;

    // Clean up existing layers
    const layersToRemove = [
      'hunt-clusters',
      'hunt-cluster-count', 
      'hunt-points',
      'location-points'
    ];

    layersToRemove.forEach(id => {
      if (map.current?.getLayer(id)) {
        map.current.removeLayer(id);
      }
    });

    ['hunts', 'locations'].forEach(id => {
      if (map.current?.getSource(id)) {
        map.current.removeSource(id);
      }
    });

    // Register features with optimizer
    optimizer.current.registerFeatures('hunts', huntsGeoJSON.features);
    optimizer.current.registerFeatures('locations', locationsGeoJSON.features);

    // Add hunt source with clustering
    if (enableClustering && huntsGeoJSON.features.length > 100) {
      const clusterConfig = optimizer.current.getClusterConfig();

      map.current.addSource('hunts', {
        type: 'geojson',
        data: huntsGeoJSON,
        cluster: true,
        clusterMaxZoom: clusterConfig.maxZoom,
        clusterRadius: clusterConfig.radius,
        clusterProperties: {
          total_birds: ['+', ['get', 'bird_count']]
        }
      });

      // Add cluster layer
      map.current.addLayer({
        id: 'hunt-clusters',
        type: 'circle',
        source: 'hunts',
        filter: ['has', 'point_count'],
        paint: {
          'circle-color': [
            'step',
            ['get', 'point_count'],
            '#51bbd6', 10,
            '#f1f075', 50,
            '#f28cb1'
          ],
          'circle-radius': [
            'step',
            ['get', 'point_count'],
            15, 10,
            20, 50,
            25
          ],
          'circle-opacity': 0.8
        }
      });

      // Add cluster count
      map.current.addLayer({
        id: 'hunt-cluster-count',
        type: 'symbol',
        source: 'hunts',
        filter: ['has', 'point_count'],
        layout: {
          'text-field': '{point_count_abbreviated}',
          'text-font': ['DIN Offc Pro Medium', 'Arial Unicode MS Bold'],
          'text-size': 12
        },
        paint: {
          'text-color': '#fff'
        }
      });

      // Register cluster layers
      optimizer.current.registerLayer('hunt-clusters', 0, 14);
      optimizer.current.registerLayer('hunt-cluster-count', 0, 14);
    } else {
      // Non-clustered source
      map.current.addSource('hunts', {
        type: 'geojson',
        data: huntsGeoJSON
      });
    }

    // Add hunt points layer
    map.current.addLayer({
      id: 'hunt-points',
      type: 'circle',
      source: 'hunts',
      filter: enableClustering ? ['!', ['has', 'point_count']] : true,
      paint: {
        'circle-radius': [
          'interpolate', ['linear'], ['zoom'],
          5, 3,
          10, 5,
          15, 8
        ],
        'circle-color': [
          'case',
          ['==', ['get', 'privacy_level'], 'public'], '#10b981',
          ['==', ['get', 'privacy_level'], 'friends'], '#3b82f6',
          ['==', ['get', 'privacy_level'], 'private'], '#ef4444',
          '#6b7280'
        ],
        'circle-stroke-width': 2,
        'circle-stroke-color': '#fff',
        'circle-opacity': [
          'interpolate', ['linear'], ['zoom'],
          5, 0.5,
          10, 0.7,
          15, 0.9
        ]
      }
    });

    optimizer.current.registerLayer('hunt-points', enableClustering ? 10 : 0, 22);

    // Add locations source and layer
    map.current.addSource('locations', {
