import NextAuth, { Session } from "next-auth"
import Google from "next-auth/providers/google"
import Facebook from "next-auth/providers/facebook"
import Credentials from "next-auth/providers/credentials"
import { JWT } from "next-auth/jwt"

export const config = {
  trustHost: true,
  basePath: "/api/auth",

  // JWT Configuration with RS256
  jwt: {
    maxAge: process.env.NODE_ENV === 'production' ? 
      60 * 60 * 4 : // 4 hours in production
      60 * 60 * 24, // 24 hours in development
  },

  providers: [
    Credentials({
      name: "credentials",
      credentials: {
        email: { label: "Email", type: "email" },
        password: { label: "Password", type: "password" }
      },
      async authorize(credentials) {
        if (!credentials?.email || !credentials?.password) {
          console.log(' NextAuth - Missing email or password credentials');
          return null;
        }

        try {
          // Authenticate with Django backend - this now returns a token
          // Environment-aware backend URL selection
          const getBackendUrl = () => {
            // 1. Check for explicit BACKEND_URL (CI and production)
            if (process.env.BACKEND_URL) {
              return `${process.env.BACKEND_URL}/api`;
            }

            // 2. Check for CI environment - use fowldata service name for container-to-container communication
            if (process.env.NEXT_PUBLIC_ENV === 'ci' || process.env.CI) {
              // In CI/Docker, frontend container needs to reach backend container via service name
              return 'http://fowldata:8000/api';
            }

            // 3. Check if running in Docker context (has internal networking)
            if (process.env.NODE_ENV === 'production' && !process.env.VERCEL) {
              return 'http://fowldata:8000/api';  // Docker internal network
            }

            // 4. Fallback to localhost for development
            return 'http://localhost:8000/api';
          };

          const API_BASE_URL = getBackendUrl();

          console.log(' NextAuth - Attempting Django authentication for:', credentials.email);
          console.log(' NextAuth - Using API endpoint:', `${API_BASE_URL}/accounts/auth/login/`);

          const response = await fetch(`${API_BASE_URL}/accounts/auth/login/`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              email: credentials.email,
              password: credentials.password,
            }),
          });

          console.log(' NextAuth - Django response status:', response.status);

          if (response.ok) {
            const userData = await response.json();
            console.log(' NextAuth - Django response data received');

            // Validate required fields from Django response
            if (!userData.email) {
              console.error(' NextAuth - Missing email in Django response');
              return null;
            }

            // Validate JWT token from backend
            if (!userData.jwt_token) {
              console.error(' NextAuth - Missing JWT token in Django response');
              return null;
            }

            // Construct name safely
            let displayName = userData.email; // fallback to email
            if (userData.first_name && userData.last_name) {
              displayName = `${userData.first_name} ${userData.last_name}`;
            } else if (userData.first_name) {
              displayName = userData.first_name;
            } else if (userData.last_name) {
              displayName = userData.last_name;
            }

            const user = {
              id: userData.id?.toString() || userData.email,
              name: displayName,
              email: userData.email,
              image: null,
              jwt_token: userData.jwt_token, // Store JWT token from backend
            };

            console.log(' NextAuth - Created user object with JWT token:', {
              id: user.id,
              name: user.name,
              email: user.email,
              hasJwtToken: !!user.jwt_token
            });

            return user;
          } else {
            const errorText = await response.text();
            console.error(' NextAuth - Django authentication failed:', {
              status: response.status,
              statusText: response.statusText,
              body: errorText
            });
            return null;
          }
        } catch (error) {
          console.error(' NextAuth - Error during authentication:', error);
          if (error instanceof TypeError) {
            console.error(' NextAuth - Network error - check if Django backend is reachable');
          }
          return null;
        }
      }
    }),

    Google({
      clientId: process.env.GOOGLE_CLIENT_ID,
      clientSecret: process.env.GOOGLE_CLIENT_SECRET,
    }),
    Facebook({
      clientId: process.env.FACEBOOK_CLIENT_ID,
      clientSecret: process.env.FACEBOOK_CLIENT_SECRET,
    }),
  ],
  callbacks: {
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    async jwt(params: any) {
      const { token, account, user, trigger } = params
      console.log(' NextAuth JWT callback:', { 
        trigger, 
        hasUser: !!user, 
        hasAccount: !!account,
        tokenSub: token.sub 
      });

      // Handle JWT creation and updates
      if (trigger === 'signIn' && user && account) {
        console.log(' NextAuth - Processing sign in for user:', user.email);

        // Store user and provider info for session
        token.sub = user.id // Subject (user ID)
        token.email = user.email
        token.name = user.name
        token.provider = account.provider

        // For credentials login, extract JWT from user object (set by authorize function)
        if (account.provider === 'credentials' && user && 'jwt_token' in user && user.jwt_token) {
          token.jwt_token = user.jwt_token
          console.log(' NextAuth - Stored JWT token from credentials login');
        }

        console.log(' NextAuth - JWT token processed successfully');
      }

      return token
    },

    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    async session(params: any) {
      const { session, token } = params
      console.log(' NextAuth Session callback:', { 
        hasSession: !!session, 
        hasToken: !!token,
        tokenSub: token.sub 
      });

      if (token && session.user) {
        // Include user data in session
        session.user.id = token.sub
        session.user.email = token.email
        session.user.name = token.name || undefined

        // Store provider and JWT token for API client
        const sessionWithExtras = session as Session & { provider?: string; jwt_token?: string }
        sessionWithExtras.provider = token.provider
        if (token.jwt_token) {
          sessionWithExtras.jwt_token = token.jwt_token
        }

        console.log(' NextAuth - Session updated with user data');
      }

      return session
    },
  },

  // Enhanced session strategy
  session: {
    strategy: "jwt" as const,
    maxAge: process.env.NODE_ENV === 'production' ? 
      60 * 60 * 4 : // 4 hours in production  
      60 * 60 * 24, // 24 hours in development
  },

  // Enhanced security and debugging
  debug: process.env.NODE_ENV === 'development',
  logger: {
    error: (code: string, metadata: unknown) => {
      console.error(` NextAuth Error [${code}]:`, metadata);
    },
    warn: (code: string) => {
      console.warn(` NextAuth Warning [${code}]`);
    },
    debug: (code: string, metadata: unknown) => {
      if (process.env.NODE_ENV === 'development') {
        console.log(` NextAuth Debug [${code}]:`, metadata);
      }
    },
  },

  pages: {
    signIn: '/auth/signin',
    error: '/auth/error',
  },
}

export const { handlers, auth, signIn, signOut } = NextAuth(config)

// Enhanced TypeScript declarations
declare module "next-auth" {
  interface Session {
    user?: {
      id?: string
      email?: string
      name?: string
    }
    accessToken?: JWT
  }

  interface User {
    id: string
    email: string
    name?: string
    provider?: string
    jwt_token?: string
  }
}

declare module "next-auth/jwt" {
  interface JWT {
    sub?: string
    email?: string
    name?: string
    provider?: string
    jwt_token?: string
  }
}

