import axios from 'axios';
import { apiClient as authApiClient } from '@/lib/auth';

const API_BASE_URL = process.env.NEXT_PUBLIC_API_BASE_URL || 'http://localhost:8000/api';

export const apiClient = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor to add Django token from our auth client
apiClient.interceptors.request.use(
  async (config) => {
    console.log(' API Client - Request interceptor called for:', config.url);
    try {
      // Ensure we have a token by syncing with NextAuth if needed
      if (!authApiClient.token) {
        console.log(' API Client - No token, syncing with NextAuth...');
        await authApiClient.syncWithNextAuth();
      }

      // Use our auth client's token for Django authentication
      if (authApiClient.token) {
        console.log(' API Client - Using Django token (first 20 chars):', authApiClient.token.substring(0, 20));
        config.headers.Authorization = `Token ${authApiClient.token}`;
      } else {
        console.log(' API Client - Still no Django token after sync attempt');
        console.warn(' API Client - Making request without authentication token!');
      }
    } catch (error) {
      console.error(' API Client - Error in request interceptor:', error);
    }

    console.log(' API Client - Final request config:', {
      url: config.url,
      hasAuth: !!config.headers.Authorization,
      method: config.method
    });

    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor to handle auth errors
apiClient.interceptors.response.use(
  (response) => response,
  async (error) => {
    if (error.response?.status === 401) {
      console.log(' API Client - 401 error, clearing token and signing out');
      // Clear our Django token
      authApiClient.setToken(null);
      // Use NextAuth signOut
      const { signOut } = await import('next-auth/react');
      await signOut({ redirect: true, callbackUrl: '/auth/signin' });
    }
    return Promise.reject(error);
  }
);

