  return useQuery({
    queryKey: ['hunt', id],
    queryFn: () => huntsApi.getHunt(id),
    enabled: !!id,
  });
}

export function useHuntStats() {
  return useQuery({
    queryKey: ['hunt-stats'],
    queryFn: huntsApi.getStats,
  });
}

export function useCreateHunt() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: (data: HuntCreate) => huntsApi.createHunt(data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['hunts'] });
      queryClient.invalidateQueries({ queryKey: ['hunt-stats'] });
    },
  });
}

export function useUpdateHunt() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: ({ id, data }: { id: number; data: Partial<HuntCreate> }) =>
