"""
Global pytest configuration and fixtures for Django tests.
"""

import pytest
from datetime import date
from django.contrib.auth import get_user_model
from django.utils import timezone
from rest_framework.test import APIClient, APIRequestFactory
from unittest.mock import Mock, patch

User = get_user_model()


def pytest_sessionfinish(session, exitstatus):
    """Clean up database connections at session end."""
    try:
        from django.db import connections

        # Close all connections
        for conn in connections.all():
            conn.close()
    except Exception:
        pass  # Ignore errors during cleanup


@pytest.fixture
def api_client():
    """Provide a Django REST Framework API client."""
    return APIClient()


@pytest.fixture
def google_user():
    """Create a test user that was authenticated via Google."""
    import uuid
    unique_suffix = uuid.uuid4().hex[:8]
    return User.objects.create(
        email=f"google_{unique_suffix}@example.com",
        username=f"googleuser_{unique_suffix}",
        first_name="Google",
        last_name="User",
    )


@pytest.fixture
def authenticated_client(api_client, user):
    """Provide an authenticated API client."""
    api_client.force_authenticate(user=user)
    return api_client


@pytest.fixture
def mock_nextauth_session_response():
    """Mock NextAuth session API response."""
    return {
        "user": {
            "email": "test@example.com",
            "name": "Test User",
            "id": "123",
        },
        "expires": "2024-12-31T23:59:59.999Z",
    }


@pytest.fixture
def mock_requests_get():
    """Mock requests.get for NextAuth session validation."""
    with patch("requests.get") as mock_get:
        yield mock_get


@pytest.fixture
def mock_nextauth_valid_session(mock_requests_get, mock_nextauth_session_response):
    """Mock successful NextAuth session validation."""
    mock_response = Mock()
    mock_response.status_code = 200
    mock_response.json.return_value = mock_nextauth_session_response
    mock_requests_get.return_value = mock_response
    return mock_requests_get


@pytest.fixture
def mock_nextauth_invalid_session(mock_requests_get):
    """Mock failed NextAuth session validation."""
    mock_response = Mock()
    mock_response.status_code = 401
    mock_requests_get.return_value = mock_response
    return mock_requests_get


@pytest.fixture
def sample_jwt_payload():
    """Sample JWT payload for testing."""
    return {
        "sub": "123",
        "email": "jwt@example.com",
        "name": "JWT User",
        "iat": 1609459200,  # Jan 1, 2021
        "exp": 1609545600,  # Jan 2, 2021
        "provider": "google",
    }


# Removed problematic autouse fixture that was causing data bleeding
# Each test file should manage its own isolation using specific fixtures


@pytest.fixture
def django_settings(settings):
    """Provide access to Django settings for modification in tests."""
    return settings


@pytest.fixture
def nextauth_test_settings(django_settings):
    """Configure Django settings for NextAuth testing."""
    django_settings.AUTHENTICATION_BACKENDS = [
        "django.contrib.auth.backends.ModelBackend",
        "accounts.nextauth_backend.NextAuthJWTBackend",
    ]
    django_settings.REST_FRAMEWORK["DEFAULT_AUTHENTICATION_CLASSES"] = [
        "accounts.authentication.NextAuthJWTAuthentication",
        "rest_framework.authentication.SessionAuthentication",
    ]
    return django_settings


# ===== USER FIXTURES =====


@pytest.fixture
def user():
    """Create a standard test user."""
    import uuid
    unique_suffix = uuid.uuid4().hex[:8]
    return User.objects.create_user(
        username=f"testuser_{unique_suffix}",
        email=f"test_{unique_suffix}@example.com",
        password="testpass123",
        first_name="Test",
        last_name="User",
    )


@pytest.fixture
def other_user():
    """Create another test user for relationship tests."""
    import uuid
    unique_suffix = uuid.uuid4().hex[:8]
    return User.objects.create_user(
        username=f"otheruser_{unique_suffix}",
        email=f"other_{unique_suffix}@example.com",
        password="testpass123",
        first_name="Other",
        last_name="User",
    )


@pytest.fixture
def users():
    """Create multiple test users for comprehensive tests."""
    import uuid
    unique_suffix = uuid.uuid4().hex[:8]
    user1 = User.objects.create(
        email=f"user1_{unique_suffix}@example.com",
        username=f"user1_{unique_suffix}",
        first_name="User",
        last_name="One"
    )
    user2 = User.objects.create(
        email=f"user2_{unique_suffix}@example.com",
        username=f"user2_{unique_suffix}",
        first_name="User",
        last_name="Two"
    )
    user3 = User.objects.create(
        email=f"user3_{unique_suffix}@example.com",
        username=f"user3_{unique_suffix}",
        first_name="User",
        last_name="Three",
    )
    return {"user1": user1, "user2": user2, "user3": user3}


@pytest.fixture
def privacy_users():
    """Create users for privacy testing with specific roles."""
    import uuid
    unique_suffix = uuid.uuid4().hex[:8]
    owner = User.objects.create(
        email=f"owner_{unique_suffix}@example.com",
        username=f"owner_{unique_suffix}",
        first_name="Owner",
        last_name="User",
    )
    friend = User.objects.create(
        email=f"friend_{unique_suffix}@example.com",
        username=f"friend_{unique_suffix}",
        first_name="Friend",
        last_name="User",
    )
    stranger = User.objects.create(
        email=f"stranger_{unique_suffix}@example.com",
        username=f"stranger_{unique_suffix}",
        first_name="Stranger",
        last_name="User",
    )
    return {"owner": owner, "friend": friend, "stranger": stranger}


@pytest.fixture
def shared_with_user():
    """Create a user for sharing tests."""
    import uuid
    unique_suffix = uuid.uuid4().hex[:8]
    return User.objects.create_user(
        username=f"shared_{unique_suffix}",
        email=f"shared_{unique_suffix}@example.com",
        password="testpass123"
    )


# ===== REQUEST/CLIENT FIXTURES =====


@pytest.fixture
def request_factory():
    """Provide Django REST framework request factory."""
    return APIRequestFactory()


# ===== MODEL FIXTURES =====


@pytest.fixture
def sample_hunt(user):
    """Create a sample hunt for testing."""
    from hunts.models import Hunt

    return Hunt.objects.create(
        user=user,
        date_of_hunt=date.today(),
        notes="Test hunt",
        privacy_level="private",
        latitude=37.7749,
        longitude=-122.4194,
    )


@pytest.fixture
def sample_location(user):
    """Create a sample location for testing."""
    from hunts.models import Location

    return Location.objects.create(
        user=user,
        name="Test Location",
        latitude=37.7749,
        longitude=-122.4194,
        privacy_level="private",
    )


@pytest.fixture
def sample_report(user):
    """Create a sample report for testing."""
    from hunts.models import Report

    return Report.objects.create(
        user=user,
        title="Test Report",
        content="Test content",
        privacy_level="private",
        latitude=37.7749,
        longitude=-122.4194,
        report_date=date.today(),
    )


@pytest.fixture
def layer_preference(user):
    """Create a user layer preference for testing."""
    from hunts.models import UserLayerPreference

    return UserLayerPreference.objects.create(
        user=user,
        layer_id="test-layer",
        layer_name="Test Layer",
        layer_type="hunt-data",
        is_visible=True,
        opacity=80,
        z_index=1,
    )


@pytest.fixture
def location_with_shares(user, shared_with_user):
    """Create a location for testing (sharing functionality removed)."""
    from hunts.models import Location

    location = Location.objects.create(
        name="Shared Location",
        latitude=37.7749,
        longitude=-122.4194,
        user=user,
        privacy_level="friends",
    )
    return location


# ===== FRIENDSHIP FIXTURES =====


@pytest.fixture
def friendship_setup(privacy_users):
    """Set up friendship relationships for testing."""
    from hunts.models import Friendship

    # Create accepted friendship between owner and friend
    friendship = Friendship.objects.create(
        from_user=privacy_users["owner"],
        to_user=privacy_users["friend"],
        status="accepted",
        accepted_at=timezone.now(),
    )

    # Add blocked user to privacy_users for consistency
    blocked_user = User.objects.create(
        email="blocked@example.com",
        username="blocked",
        first_name="Blocked",
        last_name="User",
    )

    # Return users directly as expected by tests
    return {
        "owner": privacy_users["owner"],
        "friend": privacy_users["friend"],
        "stranger": privacy_users["stranger"],
        "blocked": blocked_user,
        "friendship": friendship,
    }


@pytest.fixture
def friendship_data():
    """Sample friendship data for API requests."""
    return {"to_user": None, "notes": "Test friendship"}


# ===== PRIVACY/CONTENT FIXTURES =====


@pytest.fixture
def test_content(privacy_users):
    """Create test content with different privacy levels."""
    from hunts.models import Hunt, Location, Report

    users = privacy_users
    content = {}

    # Private content
    private_hunt = Hunt.objects.create(
        user=users["owner"],
        date_of_hunt=date.today(),
        notes="Private hunt",
        privacy_level="private",
        latitude=37.7749,
        longitude=-122.4194,
    )

    private_location = Location.objects.create(
        user=users["owner"],
        name="Private Location",
        privacy_level="private",
        latitude=37.7749,
        longitude=-122.4194,
    )

    private_report = Report.objects.create(
        user=users["owner"],
        title="Private Report",
        content="Private content",
        privacy_level="private",
        latitude=37.7749,
        longitude=-122.4194,
        report_date=date.today(),
    )

    # Friends-only content
    friends_hunt = Hunt.objects.create(
        user=users["owner"],
        date_of_hunt=date.today(),
        notes="Friends hunt",
        privacy_level="friends",
        latitude=37.7749,
        longitude=-122.4194,
    )

    friends_location = Location.objects.create(
        user=users["owner"],
        name="Friends Location",
        privacy_level="friends",
        latitude=37.7749,
        longitude=-122.4194,
    )

    friends_report = Report.objects.create(
        user=users["owner"],
        title="Friends Report",
        content="Friends content",
        privacy_level="friends",
        latitude=37.7749,
        longitude=-122.4194,
        report_date=date.today(),
    )

    # Public content
    public_hunt = Hunt.objects.create(
        user=users["owner"],
        date_of_hunt=date.today(),
        notes="Public hunt",
        privacy_level="public",
        latitude=37.7749,
        longitude=-122.4194,
    )

    public_location = Location.objects.create(
        user=users["owner"],
        name="Public Location",
        privacy_level="public",
        latitude=37.7749,
        longitude=-122.4194,
    )

    public_report = Report.objects.create(
        user=users["owner"],
        title="Public Report",
        content="Public content",
        privacy_level="public",
        latitude=37.7749,
        longitude=-122.4194,
        report_date=date.today(),
    )

    # Organize content by privacy level as expected by tests
    content = {
        "private": {
            "hunt": private_hunt,
            "location": private_location,
            "report": private_report,
        },
        "friends": {
            "hunt": friends_hunt,
            "location": friends_location,
            "report": friends_report,
        },
        "public": {
            "hunt": public_hunt,
            "location": public_location,
            "report": public_report,
        },
    }

    return content


# ===== VALIDATION DATA FIXTURES =====


@pytest.fixture
def valid_create_data():
    """Valid data for creating layer preferences."""
    return {
        "layer_id": "new-layer",
        "layer_name": "New Layer",
        "layer_type": "hunt-data",
        "is_visible": True,
        "opacity": 75,
        "z_index": 2,
