    return {
        "layer_ids": ["layer1", "layer2", "layer3"],
        "is_visible": False,
        "layer_type": "hunt-data",
    }


# ===== MOCK FIXTURES =====


@pytest.fixture
def mock_s3_manager():
    """Mock S3Manager to avoid actual S3 operations."""
    with patch("hunts.s3_manager.s3_manager") as mock:
        mock.generate_presigned_download_url.return_value = (
            "https://mock.s3.url/photo.jpg"
        )
        mock.generate_presigned_upload_url.return_value = {
            "url": "https://mock.s3.url/upload",
            "fields": {"key": "test/photo.jpg"},
        }
        yield mock


@pytest.fixture
def mock_weather_api():
    """Mock weather API responses."""
    with patch("requests.get") as mock_get:
        mock_response = Mock()
        mock_response.status_code = 200
        mock_response.json.return_value = {
            "current": {
                "temp_f": 72.0,
                "wind_mph": 5.0,
                "wind_dir": "NW",
                "condition": {"text": "Clear"},
            }
        }
        mock_get.return_value = mock_response
        yield mock_get

