# Comprehensive cleanup of legacy fields and database alignment

from django.db import migrations, models
from django.conf import settings
import django.db.models.deletion


def comprehensive_database_cleanup(apps, schema_editor):
    """Comprehensive database cleanup and alignment with current models"""
    with schema_editor.connection.cursor() as cursor:
        try:
            # First, check what tables exist
            cursor.execute(
                """
                SELECT table_name 
                FROM information_schema.tables 
                WHERE table_schema = 'public' AND table_name LIKE 'hunts_%';
            """
            )
            existing_tables = [row[0] for row in cursor.fetchall()]

            # Clean up Location table if it exists
            if "hunts_location" in existing_tables:
                cursor.execute(
                    """
                    SELECT column_name 
                    FROM information_schema.columns 
                    WHERE table_name = 'hunts_location';
                """
                )
                location_columns = [row[0] for row in cursor.fetchall()]

                # Remove legacy columns
                legacy_location_columns = ["is_shared", "photo_count", "shared_with"]
                for col in legacy_location_columns:
                    if col in location_columns:
                        cursor.execute(
                            f"ALTER TABLE hunts_location DROP COLUMN {col} CASCADE;"
                        )

                # Ensure required columns have proper values
                if "name" in location_columns:
                    cursor.execute(
                        """
                        UPDATE hunts_location 
                        SET name = 'Location ' || id 
                        WHERE name IS NULL OR name = '';
                    """
                    )

            # Clean up Report table if it exists
            if "hunts_report" in existing_tables:
                cursor.execute(
                    """
                    SELECT column_name 
                    FROM information_schema.columns 
                    WHERE table_name = 'hunts_report';
                """
                )
                report_columns = [row[0] for row in cursor.fetchall()]

                # Remove legacy columns
                legacy_report_columns = [
                    "text",
                    "comment_count",
                    "photo_count",
                    "location_name",
                    "hunt_id",
                    "sharing_policy_id",
                    "weather_id",
                ]
                for col in legacy_report_columns:
                    if col in report_columns:
                        cursor.execute(
                            f"ALTER TABLE hunts_report DROP COLUMN {col} CASCADE;"
                        )

            # Clean up any other legacy tables that might exist
            legacy_tables = [
                "hunts_locationshare",
                "hunts_sharingpolicy",
                "hunts_reportcomment",
                "hunts_weather",
                "hunts_weathertask",
            ]
            for table in legacy_tables:
                if table in existing_tables:
                    cursor.execute(f"DROP TABLE {table} CASCADE;")

        except Exception as e:
            # Log the error but don't fail the migration
            print(f"Migration warning: {e}")


